import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';
import { ethers } from 'ethers';

const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";

const MYSTERY_BOX_ABI = [
  "function getNFTAttributes(uint256 tokenId) external view returns (tuple(uint8 rarity, uint256 power, uint256 dailyReward, uint256 maxReward, uint256 minedAmount, bool isStaked, uint256 stakeTime))",
  "function getStakedNFTs(address user) external view returns (uint256[])",
  "function calculateReward(uint256 tokenId) public view returns (uint256)",
  "function unstakeNFT(uint256 tokenId) external"
];

const NFT_RARITY = ['N', 'R', 'SR', 'SSR'];

const NFT_SETTINGS = {
  N: { power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MINING_LEVELS = [
  { name: 'LV1', min: 0, max: 10000, bonus: 0.10, requiredPeople: 5, bonusPerPerson: 0.05 },
  { name: 'LV2', min: 10001, max: 30000, bonus: 0.15, requiredPeople: 8, bonusPerPerson: 0.08 },
  { name: 'LV3', min: 30001, max: 100000, bonus: 0.20, requiredPeople: 12, bonusPerPerson: 0.12 },
  { name: 'LV4', min: 100001, max: 500000, bonus: 0.30, requiredPeople: 18, bonusPerPerson: 0.18 },
  { name: 'LV5', min: 500001, max: Infinity, bonus: 0.50, requiredPeople: 25, bonusPerPerson: 0.25 }
];

const NFT_PROBABILITIES = {
  N: 0.55,
  R: 0.15,
  SR: 0.05,
  SSR: 0.01
};

const NFTMining = () => {
  const { active, account, activate, library } = useWeb3React();
  const [miningStats, setMiningStats] = useState({
    totalPower: 0,
    personalPower: 0,
    efficiency: 0,
    currentRewards: 0,
    dailyRewards: 0,
    teamMembers: 0,
    miningLevel: 'LV1'
  });
  const [nfts, setNfts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const loadMiningData = async () => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());

      // 加载质押的NFT
      const stakedTokenIds = await contract.getStakedNFTs(account);
      const stakedNFTsData = await Promise.all(
        stakedTokenIds.map(async (tokenId) => {
          const attributes = await contract.getNFTAttributes(tokenId);
          const reward = await contract.calculateReward(tokenId);

          return {
            id: tokenId.toString(),
            type: NFT_RARITY[attributes.rarity],
            power: attributes.power.toString(),
            dailyReward: ethers.utils.formatEther(attributes.dailyReward),
            maxReward: ethers.utils.formatEther(attributes.maxReward),
            minedAmount: ethers.utils.formatEther(attributes.minedAmount),
            currentReward: ethers.utils.formatEther(reward)
          };
        })
      );
      setNfts(stakedNFTsData);

      // 计算总算力和每日收益
      const totalPower = stakedNFTsData.reduce((sum, nft) => sum + parseInt(nft.power), 0);
      const dailyRewards = stakedNFTsData.reduce((sum, nft) => sum + parseFloat(nft.dailyReward), 0);

      setMiningStats(prev => ({
        ...prev,
        personalPower: totalPower,
        dailyRewards
      }));

      setIsLoading(false);
    } catch (error) {
      console.error('Error loading mining data:', error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (active) {
      loadMiningData();
    }
  }, [active, account]);

  const handleUnstake = async (nftId) => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await contract.unstakeNFT(nftId);
      await tx.wait();

      alert('解除质押成功！');
      loadMiningData();
    } catch (error) {
      console.error('Error unstaking NFT:', error);
      alert('解除质押失败: ' + error.message);
    }
  };

  const calculateMiningLevel = (power) => {
    const level = MINING_LEVELS.find(l => power >= l.min && power <= l.max);
    return level || MINING_LEVELS[0];
  };

  const calculateTeamBonus = (power, members) => {
    const level = calculateMiningLevel(power);
    return Math.min(members, level.requiredPeople) * level.bonusPerPerson;
  };

  return (
    <div className="max-w-4xl mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-binance-yellow mb-6">NFT 挖矿</h1>

      {!active ? (
        <button
          onClick={connectWallet}
          className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
        >
          连接钱包
        </button>
      ) : (
        <div className="space-y-6">
          {isLoading ? (
            <div className="text-center py-8">加载中...</div>
          ) : (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">全网算力</h3>
                  <p className="text-xl font-bold">{miningStats.totalPower.toLocaleString()}</p>
                </div>
                <div className="bg-gray-900 p-4 rounded">
                  <h3 className="text-sm text-gray-400">我的算力</h3>
                  <div className="flex items-center">
                    <p className="text-xl font-bold text-white">{miningStats.personalPower}</p>
                    <div className="flex items-center ml-2">
                      <span className="text-yellow-500 text-xs mr-1">★</span>
                      <span className="text-gray-400 text-xs">{miningStats.miningLevel}</span>
                    </div>
                  </div>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">挖矿效率</h3>
                  <p className="text-xl font-bold">{(miningStats.efficiency * 100).toFixed(2)}%</p>
                  <p className="text-xs text-gray-500">团队: {miningStats.teamMembers}人</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">当前收益</h3>
                  <p className="text-xl font-bold">{miningStats.currentRewards.toFixed(4)} ZONE</p>
                  <p className="text-xs text-gray-500">日收益: {miningStats.dailyRewards.toFixed(2)} ZONE</p>
                </div>
              </div>

              <div className="bg-gray-800 p-4 rounded-lg">
                <h2 className="text-lg font-bold mb-4">质押中的 NFT</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {nfts.map(nft => (
                    <div key={nft.id} className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex justify-between items-center mb-2">
                        <span className="text-binance-yellow font-bold">{nft.type}</span>
                        <span className="text-sm text-gray-400">#{nft.id}</span>
                      </div>
                      <div className="space-y-2">
                        <p className="text-sm">基础算力: {nft.power}</p>
                        <p className="text-sm">日收益: {nft.dailyReward} ZONE</p>
                        <p className="text-sm">已挖矿量: {nft.minedAmount} ZONE</p>
                        <p className="text-sm">当前收益: {nft.currentReward} ZONE</p>
                        <p className="text-sm">最大收益: {nft.maxReward} ZONE</p>
                        <div className="w-full bg-gray-600 rounded-full h-2 mb-2">
                          <div
                            className="bg-binance-yellow h-2 rounded-full transition-all duration-500"
                            style={{
                              width: `${(parseFloat(nft.minedAmount) / parseFloat(nft.maxReward)) * 100}%`
                            }}
                          />
                        </div>
                        <button
                          onClick={() => handleUnstake(nft.id)}
                          className="w-full bg-red-500 text-white font-bold py-2 px-4 rounded-lg hover:opacity-90 transition-opacity"
                        >
                          解除质押
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="bg-gray-800 p-4 rounded-lg">
                <h2 className="text-lg font-bold mb-4">挖矿状态</h2>
                <div className="w-full bg-gray-700 rounded-full h-4">
                  <div
                    className="bg-binance-yellow h-4 rounded-full transition-all duration-500"
                    style={{ width: `${(miningStats.personalPower / miningStats.totalPower * 100).toFixed(2)}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-400 mt-2">
                  占全网算力: {(miningStats.personalPower / miningStats.totalPower * 100).toFixed(2)}%
                </p>
                <div className="mt-4 text-sm text-gray-400">
                  <p>当前等级: {miningStats.miningLevel} ({calculateMiningLevel(miningStats.personalPower).bonus * 100}% 加成)</p>
                  <p>算力范围: {calculateMiningLevel(miningStats.personalPower).min.toLocaleString()} - {calculateMiningLevel(miningStats.personalPower).max.toLocaleString()}</p>
                  <p>团队要求: {calculateMiningLevel(miningStats.personalPower).requiredPeople}人</p>
                  <p>每人贡献: {(calculateMiningLevel(miningStats.personalPower).bonusPerPerson * 100).toFixed(1)}%</p>
                </div>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default NFTMining;
