import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const NFT_SETTINGS = {
  N: { power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MINING_LEVELS = [
  { name: 'LV1', min: 0, max: 10000, bonus: 0.10, requiredPeople: 5, bonusPerPerson: 0.05 },
  { name: 'LV2', min: 10001, max: 30000, bonus: 0.15, requiredPeople: 8, bonusPerPerson: 0.08 },
  { name: 'LV3', min: 30001, max: 100000, bonus: 0.20, requiredPeople: 12, bonusPerPerson: 0.12 },
  { name: 'LV4', min: 100001, max: 500000, bonus: 0.30, requiredPeople: 18, bonusPerPerson: 0.18 },
  { name: 'LV5', min: 500001, max: Infinity, bonus: 0.50, requiredPeople: 25, bonusPerPerson: 0.25 }
];

const NFT_PROBABILITIES = {
  N: 0.55,
  R: 0.15,
  SR: 0.05,
  SSR: 0.01
};

const NFTMining = () => {
  const { active, account, activate } = useWeb3React();
  const [miningStats, setMiningStats] = useState({
    totalPower: 0,
    personalPower: 0,
    efficiency: 0,
    currentRewards: 0,
    dailyRewards: 0,
    teamMembers: 0,
    miningLevel: 'LV1'
  });
  const [nfts, setNfts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const calculateMiningLevel = (power) => {
    const level = MINING_LEVELS.find(l => power >= l.min && power <= l.max);
    return level || MINING_LEVELS[0];
  };

  const calculateTeamBonus = (power, members) => {
    const level = calculateMiningLevel(power);
    return Math.min(members, level.requiredPeople) * level.bonusPerPerson;
  };

  // 模拟获取挖矿数据
  useEffect(() => {
    if (active) {
      const personalPower = 5000;
      const totalPower = 1000000;
      const teamMembers = 6;
      const level = calculateMiningLevel(personalPower);
      const teamBonus = calculateTeamBonus(personalPower, teamMembers);

      setMiningStats({
        totalPower,
        personalPower,
        efficiency: teamBonus,
        currentRewards: 100,
        dailyRewards: personalPower * 0.028 * (1 + teamBonus),
        teamMembers,
        miningLevel: level.name
      });

      // 模拟用户的NFT数据
      setNfts([
        { id: 1, type: 'SSR', ...NFT_SETTINGS.SSR },
        { id: 2, type: 'SR', ...NFT_SETTINGS.SR },
        { id: 3, type: 'R', ...NFT_SETTINGS.R },
        { id: 4, type: 'N', ...NFT_SETTINGS.N }
      ]);

      setIsLoading(false);
    }
  }, [active]);

  // 模拟实时挖矿效果
  useEffect(() => {
    if (active) {
      const interval = setInterval(() => {
        setMiningStats(prev => ({
          ...prev,
          currentRewards: prev.currentRewards + (prev.dailyRewards / (24 * 60 * 60))
        }));
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [active]);

  return (
    <div className="max-w-4xl mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-binance-yellow mb-6">NFT 挖矿</h1>

      {!active ? (
        <button
          onClick={connectWallet}
          className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
        >
          连接钱包
        </button>
      ) : (
        <div className="space-y-6">
          {isLoading ? (
            <div className="text-center py-8">加载中...</div>
          ) : (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">全网算力</h3>
                  <p className="text-xl font-bold">{miningStats.totalPower.toLocaleString()}</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">我的算力</h3>
                  <p className="text-xl font-bold">{miningStats.personalPower.toLocaleString()}</p>
                  <p className="text-xs text-gray-500">等级: {miningStats.miningLevel}</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">挖矿效率</h3>
                  <p className="text-xl font-bold">{(miningStats.efficiency * 100).toFixed(2)}%</p>
                  <p className="text-xs text-gray-500">团队: {miningStats.teamMembers}人</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">当前收益</h3>
                  <p className="text-xl font-bold">{miningStats.currentRewards.toFixed(4)} ZONE</p>
                  <p className="text-xs text-gray-500">日收益: {miningStats.dailyRewards.toFixed(2)} ZONE</p>
                </div>
              </div>

              <div className="bg-gray-800 p-4 rounded-lg">
                <h2 className="text-lg font-bold mb-4">我的 NFT</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {nfts.map(nft => (
                    <div key={nft.id} className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex justify-between items-center mb-2">
                        <span className="text-binance-yellow font-bold">{nft.type}</span>
                        <span className="text-sm text-gray-400">#{nft.id}</span>
                      </div>
                      <div className="space-y-1">
                        <p className="text-sm">基础算力: {nft.power}</p>
                        <p className="text-sm">开盒价: {nft.price} ZONE</p>
                        <p className="text-sm">日收益: {nft.dailyReward} ZONE</p>
                        <p className="text-xs text-gray-500">回本天数: {nft.roi}天</p>
                        <p className="text-xs text-gray-500">年化收益: {nft.yearReturn}%</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="bg-gray-800 p-4 rounded-lg">
                <h2 className="text-lg font-bold mb-4">挖矿状态</h2>
                <div className="w-full bg-gray-700 rounded-full h-4">
                  <div
                    className="bg-binance-yellow h-4 rounded-full transition-all duration-500"
                    style={{ width: `${(miningStats.personalPower / miningStats.totalPower * 100).toFixed(2)}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-400 mt-2">
                  占全网算力: {(miningStats.personalPower / miningStats.totalPower * 100).toFixed(2)}%
                </p>
                <div className="mt-4 text-sm text-gray-400">
                  <p>当前等级: {miningStats.miningLevel} ({calculateMiningLevel(miningStats.personalPower).bonus * 100}% 加成)</p>
                  <p>算力范围: {calculateMiningLevel(miningStats.personalPower).min.toLocaleString()} - {calculateMiningLevel(miningStats.personalPower).max.toLocaleString()}</p>
                  <p>团队要求: {calculateMiningLevel(miningStats.personalPower).requiredPeople}人</p>
                  <p>每人贡献: {(calculateMiningLevel(miningStats.personalPower).bonusPerPerson * 100).toFixed(1)}%</p>
                </div>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default NFTMining;
