import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < 0.1 || bnbAmount > 0.5) {
      alert('请输入0.1-0.5 BNB之间的数量');
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="max-w-md mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-binance-yellow mb-6">AIMiner IDO私募</h1>

      {!active ? (
        <button
          onClick={connectWallet}
          className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
        >
          连接钱包
        </button>
      ) : (
        <div className="space-y-4">
          <div className="bg-gray-800 p-4 rounded-lg">
            <p className="text-sm text-gray-400">当前钱包地址</p>
            <p className="font-mono">{account}</p>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">
              参与数量 (BNB)
            </label>
            <input
              type="number"
              min="0.1"
              max="0.5"
              step="0.1"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="w-full bg-gray-800 border border-gray-700 rounded-lg px-4 py-2 text-white"
              placeholder="输入 0.1-0.5 BNB"
            />
          </div>

          <div className="text-sm text-gray-400">
            <p>• 最小参与: 0.1 BNB</p>
            <p>• 最大参与: 0.5 BNB</p>
          </div>

          <button
            onClick={handleContribute}
            className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
          >
            确认参与
          </button>
        </div>
      )}
    </div>
  );
};

export default IDO;
