import React from 'react';

const NFTHistory = ({ history }) => {
  if (!history || history.length === 0) {
    return (
      <div className="text-center text-gray-400 py-8">
        暂无交易记录
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {history.map((trade, index) => (
        <div key={index} className="bg-gray-800 rounded-lg p-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <span className="text-gray-400">卖家:</span>
              <span className="ml-2 text-white">{trade.seller.slice(0, 6)}...{trade.seller.slice(-4)}</span>
            </div>
            <div>
              <span className="text-gray-400">买家:</span>
              <span className="ml-2 text-white">{trade.buyer.slice(0, 6)}...{trade.buyer.slice(-4)}</span>
            </div>
            <div>
              <span className="text-gray-400">价格:</span>
              <span className="ml-2 text-white">{trade.price} BNB</span>
            </div>
            <div>
              <span className="text-gray-400">时间:</span>
              <span className="ml-2 text-white">{new Date(trade.timestamp * 1000).toLocaleString()}</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default NFTHistory;
