const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";

// 代币ABI
const ZONE_TOKEN_ABI = [
  "function approve(address spender, uint256 amount) public returns (bool)",
  "function allowance(address owner, address spender) public view returns (uint256)"
];

// 盲盒ABI
const MYSTERY_BOX_ABI = [
  "function openBox() public returns (uint8)",
  "function BOX_PRICE() public view returns (uint256)"
];

const NFT_SETTINGS = {
  N: { probability: 0.55, power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { probability: 0.15, power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { probability: 0.05, power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { probability: 0.01, power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MysteryBox = () => {
  const { active, account, activate, library } = useWeb3React();
  const [openingResult, setOpeningResult] = useState(null);
  const [isOpening, setIsOpening] = useState(false);
  const [isApproving, setIsApproving] = useState(false);
  const [showAnimation, setShowAnimation] = useState(false);

  useEffect(() => {
    if (active && library) {
      // fetchBoxPrice();
    }
  }, [active, library]);

  // const fetchBoxPrice = async () => {
  //   try {
  //     const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
  //     const price = await boxContract.BOX_PRICE();
  //     setBoxPrice(ethers.utils.formatEther(price));
  //   } catch (error) {
  //     console.error('Error fetching box price:', error);
  //   }
  // };

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const checkAllowance = async () => {
    try {
      const tokenContract = new ethers.Contract(ZONE_TOKEN_ADDRESS, ZONE_TOKEN_ABI, library.getSigner());
      const allowance = await tokenContract.allowance(account, MYSTERY_BOX_ADDRESS);
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const boxPrice = await boxContract.BOX_PRICE();
      return allowance.gte(boxPrice);
    } catch (error) {
      console.error('Error checking allowance:', error);
      return false;
    }
  };

  const approveToken = async () => {
    try {
      setIsApproving(true);
      const tokenContract = new ethers.Contract(ZONE_TOKEN_ADDRESS, ZONE_TOKEN_ABI, library.getSigner());
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const boxPrice = await boxContract.BOX_PRICE();

      const approveAmount = boxPrice.mul(1000);
      const tx = await tokenContract.approve(MYSTERY_BOX_ADDRESS, approveAmount);
      await tx.wait();

      setIsApproving(false);
      return true;
    } catch (error) {
      console.error('Error approving token:', error);
      setIsApproving(false);
      return false;
    }
  };

  const openBox = async () => {
    if (!active) {
      alert('请先连接钱包');
      return;
    }

    try {
      setIsOpening(true);
      setShowAnimation(true);

      const hasAllowance = await checkAllowance();
      if (!hasAllowance) {
        const approved = await approveToken();
        if (!approved) {
          setIsOpening(false);
          setShowAnimation(false);
          alert('授权失败');
          return;
        }
      }

      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await boxContract.openBox();
      const receipt = await tx.wait();

      // 模拟开箱动画
      setTimeout(() => {
        setShowAnimation(false);
        setOpeningResult('SSR'); // 这里应该根据实际结果设置
        setIsOpening(false);
      }, 3000);

    } catch (error) {
      console.error('Error opening box:', error);
      setIsOpening(false);
      setShowAnimation(false);
      alert('开箱失败');
    }
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 科技感背景 */}
      <div className="absolute inset-0">
        {/* 六边形网格 */}
        <div 
          className="absolute inset-0 opacity-10"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M30 0l25.98 15v30L30 60 4.02 45V15z' stroke-width='2' stroke='%23ffffff' fill='none' fill-rule='evenodd'/%3E%3C/svg%3E")`,
            backgroundSize: '60px 60px'
          }}
        ></div>

        {/* 扫描线效果 */}
        <div 
          className="absolute inset-0 animate-scan"
          style={{
            background: 'linear-gradient(to bottom, transparent, rgba(255,255,255,0.05) 50%, transparent)',
            backgroundSize: '100% 200%',
            animation: 'scan 4s linear infinite'
          }}
        ></div>

        {/* 科技光束 */}
        <div className="absolute -top-40 -left-40 w-80 h-80 bg-blue-500/20 rotate-45 blur-[100px] animate-pulse"></div>
        <div className="absolute -bottom-40 -right-40 w-80 h-80 bg-yellow-500/20 rotate-45 blur-[100px] animate-pulse delay-1000"></div>
      </div>

      {/* 页面内容 */}
      <div className="relative py-16">
        <div className="container mx-auto px-4">
          {/* 头部信息 */}
          <div className="text-center mb-16 relative">
            <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-32 h-32 bg-yellow-500/10 rounded-full blur-xl animate-pulse"></div>
            <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 via-yellow-500 to-yellow-600 mb-4 relative">
              神秘盲盒
              <div className="absolute -inset-1 bg-yellow-500/20 blur-sm rounded-lg -z-10"></div>
            </h1>
            <p className="text-xl text-blue-400/80">开启盲盒，获得稀有 NFT</p>
          </div>

          {/* 主要内容区 */}
          <div className="max-w-3xl mx-auto">
            {/* 主内容卡片 */}
            <div className="relative group">
              {/* 卡片边框动画 */}
              <div className="absolute -inset-0.5 bg-gradient-to-r from-yellow-500 to-blue-500 rounded-2xl blur opacity-30 group-hover:opacity-50 transition duration-1000"></div>

              {/* 卡片内容 */}
              <div className="relative bg-[#1A2438]/80 backdrop-blur-xl rounded-2xl p-8">
                {/* 装饰线条 */}
                <div className="absolute top-0 left-0 w-20 h-20">
                  <div className="absolute top-0 left-0 w-2 h-8 bg-gradient-to-b from-yellow-500/50 to-transparent"></div>
                  <div className="absolute top-0 left-0 h-2 w-8 bg-gradient-to-r from-yellow-500/50 to-transparent"></div>
                </div>
                <div className="absolute bottom-0 right-0 w-20 h-20 transform rotate-180">
                  <div className="absolute top-0 left-0 w-2 h-8 bg-gradient-to-b from-yellow-500/50 to-transparent"></div>
                  <div className="absolute top-0 left-0 h-2 w-8 bg-gradient-to-r from-yellow-500/50 to-transparent"></div>
                </div>

                {/* 盲盒图片 */}
                <div className="aspect-square relative mb-12">
                  <div 
                    className="absolute inset-0 bg-contain bg-center bg-no-repeat"
                    style={{ 
                      backgroundImage: 'url(/images/mystery-box/box-bg.png)'
                    }}
                  />
                </div>

                {/* 价格显示 */}
                <div className="text-center mb-8">
                  <div className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-600">
                    100 ZONE
                  </div>
                </div>

                {/* 开启按钮 */}
                {active ? (
                  <button
                    onClick={openBox}
                    disabled={isOpening || isApproving}
                    className={`w-full max-w-xs mx-auto relative group overflow-hidden bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-4 px-8 rounded-xl hover:from-yellow-400 hover:to-yellow-300 transition-all flex items-center justify-center ${
                      (isOpening || isApproving) ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    <span className="relative z-10">
                      {isApproving ? '授权中' : isOpening ? '开启中' : '开启盲盒'}
                    </span>
                    <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/20 to-transparent transition-transform duration-1000"></div>
                  </button>
                ) : (
                  <button
                    onClick={connectWallet}
                    className="w-full max-w-xs mx-auto relative group overflow-hidden bg-[#1A2438] text-yellow-500 border-2 border-yellow-500 font-bold py-4 px-8 rounded-xl hover:bg-yellow-500 hover:text-black transition-all"
                  >
                    <span className="relative z-10">连接钱包</span>
                    <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/20 to-transparent transition-transform duration-1000"></div>
                  </button>
                )}
              </div>
            </div>
          </div>

          {/* NFT概率展示 */}
          <div className="max-w-6xl mx-auto mt-16">
            <h2 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-600 mb-8 text-center relative">
              NFT 概率
              <div className="absolute -inset-1 bg-yellow-500/20 blur-sm rounded-lg -z-10"></div>
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {Object.entries(NFT_SETTINGS).map(([rarity, data]) => (
                <div key={rarity} className="group relative">
                  <div className="absolute -inset-0.5 bg-gradient-to-r from-yellow-500 to-blue-500 rounded-xl blur opacity-30 group-hover:opacity-50 transition duration-1000"></div>
                  <div className="relative bg-[#1A2438]/80 backdrop-blur-xl rounded-xl p-6 border border-white/5">
                    <div className={`text-2xl font-bold mb-4 ${
                      rarity === 'SSR' ? 'text-yellow-500' :
                      rarity === 'SR' ? 'text-purple-500' :
                      rarity === 'R' ? 'text-blue-500' :
                      'text-gray-500'
                    }`}>
                      {rarity}
                    </div>
                    <div className="text-blue-400/80">
                      <div className="mb-2">
                        <span className="text-sm">概率：</span>
                        <span className="text-lg font-bold">{data.probability * 100}%</span>
                      </div>
                      <div>
                        <span className="text-sm">数量：</span>
                        <span className="text-lg font-bold">{data.count}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* 开箱结果弹窗 */}
      {openingResult && (
        <div className="fixed inset-0 bg-black/90 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="relative group">
            <div className="absolute -inset-0.5 bg-gradient-to-r from-yellow-500 to-blue-500 rounded-2xl blur opacity-50"></div>
            <div className="relative bg-[#1A2438]/90 backdrop-blur-xl rounded-2xl p-8 max-w-md w-full mx-4 border border-white/10">
              <button 
                onClick={() => setOpeningResult(null)}
                className="absolute top-4 right-4 text-gray-400 hover:text-white"
              >
                ✕
              </button>
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-400/80 mb-4">恭喜获得</div>
                <div className={`text-4xl font-bold mb-6 ${
                  openingResult === 'SSR' ? 'text-yellow-500' :
                  openingResult === 'SR' ? 'text-purple-500' :
                  openingResult === 'R' ? 'text-blue-500' :
                  'text-gray-500'
                }`}>
                  {openingResult} NFT
                </div>
                <div className="w-48 h-48 bg-gray-800 rounded-xl mx-auto mb-6 relative overflow-hidden">
                  <div className="absolute inset-0 bg-gradient-to-b from-transparent via-blue-500/10 to-transparent animate-scan"></div>
                </div>
                <button
                  onClick={() => setOpeningResult(null)}
                  className="bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-3 px-8 rounded-xl hover:from-yellow-400 hover:to-yellow-300 transition-colors relative group overflow-hidden"
                >
                  <span className="relative z-10">确定</span>
                  <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/20 to-transparent transition-transform duration-1000"></div>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* 添加样式 */}
      <style jsx>{`
        @keyframes scan {
          0% { background-position: 0 -200%; }
          100% { background-position: 0 200%; }
        }
      `}</style>
    </div>
  );
};

export default MysteryBox;
