import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const NFT_SETTINGS = {
  N: { probability: 0.55, power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { probability: 0.15, power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { probability: 0.05, power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { probability: 0.01, power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MysteryBox = () => {
  const { active, account, activate } = useWeb3React();
  const [openingResult, setOpeningResult] = useState(null);
  const [isOpening, setIsOpening] = useState(false);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const openBox = async () => {
    if (!active) {
      alert('请先连接钱包');
      return;
    }

    setIsOpening(true);

    // 模拟开箱过程
    setTimeout(() => {
      const random = Math.random();
      let result = null;

      if (random <= NFT_SETTINGS.SSR.probability) {
        result = { type: 'SSR', ...NFT_SETTINGS.SSR };
      } else if (random <= NFT_SETTINGS.SSR.probability + NFT_SETTINGS.SR.probability) {
        result = { type: 'SR', ...NFT_SETTINGS.SR };
      } else if (random <= NFT_SETTINGS.SSR.probability + NFT_SETTINGS.SR.probability + NFT_SETTINGS.R.probability) {
        result = { type: 'R', ...NFT_SETTINGS.R };
      } else {
        result = { type: 'N', ...NFT_SETTINGS.N };
      }

      setOpeningResult(result);
      setIsOpening(false);
    }, 2000);
  };

  return (
    <div className="max-w-md mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-binance-yellow mb-6">神秘宝箱</h1>

      {!active ? (
        <button
          onClick={connectWallet}
          className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
        >
          连接钱包
        </button>
      ) : (
        <div className="space-y-6">
          <div className="bg-gray-800 p-4 rounded-lg">
            <p className="text-sm text-gray-400">当前钱包地址</p>
            <p className="font-mono">{account}</p>
          </div>

          <div className="text-center">
            <button
              onClick={openBox}
              disabled={isOpening}
              className={`w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity ${
                isOpening ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {isOpening ? '开启中...' : '开启宝箱 (100 ZONE)'}
            </button>
          </div>

          {openingResult && (
            <div className="bg-gray-800 p-4 rounded-lg text-center">
              <h3 className="text-xl font-bold mb-2">开箱结果</h3>
              <p className="text-2xl text-binance-yellow mb-2">
                恭喜获得 {openingResult.type} NFT!
              </p>
              <div className="space-y-2 text-sm">
                <p>基础算力: {openingResult.power}</p>
                <p>日收益: {openingResult.dailyReward} ZONE</p>
                <p>回本天数: {openingResult.roi}天</p>
                <p>年化收益: {openingResult.yearReturn}%</p>
              </div>
            </div>
          )}

          <div className="text-sm text-gray-400">
            <p className="font-bold mb-2">NFT稀有度说明:</p>
            <ul className="space-y-2">
              <li className="flex justify-between">
                <span>• SSR - 超级稀有 (1%)</span>
                <span>算力: 6400</span>
              </li>
              <li className="flex justify-between">
                <span>• SR - 稀有 (5%)</span>
                <span>算力: 1600</span>
              </li>
              <li className="flex justify-between">
                <span>• R - 普通稀有 (15%)</span>
                <span>算力: 400</span>
              </li>
              <li className="flex justify-between">
                <span>• N - 普通 (55%)</span>
                <span>算力: 100</span>
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default MysteryBox;
