import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const NFT_POWER_LEVELS = {
  N: 100,
  R: 400,
  SR: 1600,
  SSR: 6400
};

const POWER_TIERS = [
  { min: 0, max: 10000, bonus: 0.10, requiredPeople: 5, bonusPerPerson: 0.05 },
  { min: 10001, max: 30000, bonus: 0.15, requiredPeople: 8, bonusPerPerson: 0.08 },
  { min: 30001, max: 100000, bonus: 0.20, requiredPeople: 12, bonusPerPerson: 0.12 },
  { min: 100001, max: 500000, bonus: 0.30, requiredPeople: 18, bonusPerPerson: 0.18 },
  { min: 500001, max: Infinity, bonus: 0.50, requiredPeople: 25, bonusPerPerson: 0.25 }
];

const DAILY_REWARD_RATE = 0.028; // 每单位算力每天的ZONE收益

const NFTMining = () => {
  const { active, account, activate } = useWeb3React();
  const [miningStats, setMiningStats] = useState({
    totalPower: 0,
    personalPower: 0,
    efficiency: 0,
    currentRewards: 0,
    dailyRewards: 0,
    teamMembers: 0
  });
  const [nfts, setNfts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const calculatePowerTierBonus = (power, members) => {
    const tier = POWER_TIERS.find(t => power >= t.min && power <= t.max);
    if (!tier) return 0;
    const memberBonus = Math.min(members, tier.requiredPeople) * tier.bonusPerPerson;
    return memberBonus;
  };

  // 模拟获取挖矿数据
  useEffect(() => {
    if (active) {
      // 这里应该从合约获取实际数据
      const personalPower = 5000;
      const totalPower = 1000000;
      const teamMembers = 6;
      const powerBonus = calculatePowerTierBonus(personalPower, teamMembers);

      setMiningStats({
        totalPower,
        personalPower,
        efficiency: powerBonus,
        currentRewards: 100,
        dailyRewards: personalPower * DAILY_REWARD_RATE * (1 + powerBonus),
        teamMembers
      });

      // 模拟用户的NFT数据
      setNfts([
        { id: 1, type: 'SSR', power: NFT_POWER_LEVELS.SSR, bonus: 50 },
        { id: 2, type: 'SR', power: NFT_POWER_LEVELS.SR, bonus: 30 },
        { id: 3, type: 'R', power: NFT_POWER_LEVELS.R, bonus: 20 }
      ]);

      setIsLoading(false);
    }
  }, [active]);

  // 模拟实时挖矿效果
  useEffect(() => {
    if (active) {
      const interval = setInterval(() => {
        setMiningStats(prev => ({
          ...prev,
          currentRewards: prev.currentRewards + (prev.dailyRewards / (24 * 60 * 60))
        }));
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [active]);

  return (
    <div className="max-w-4xl mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-binance-yellow mb-6">NFT 挖矿</h1>

      {!active ? (
        <button
          onClick={connectWallet}
          className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
        >
          连接钱包
        </button>
      ) : (
        <div className="space-y-6">
          {isLoading ? (
            <div className="text-center py-8">加载中...</div>
          ) : (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">全网算力</h3>
                  <p className="text-xl font-bold">{miningStats.totalPower.toLocaleString()}</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">我的算力</h3>
                  <p className="text-xl font-bold">{miningStats.personalPower.toLocaleString()}</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">挖矿效率</h3>
                  <p className="text-xl font-bold">{(miningStats.efficiency * 100).toFixed(2)}%</p>
                  <p className="text-xs text-gray-500">团队: {miningStats.teamMembers}人</p>
                </div>
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="text-sm text-gray-400">当前收益</h3>
                  <p className="text-xl font-bold">{miningStats.currentRewards.toFixed(4)} ZONE</p>
                  <p className="text-xs text-gray-500">日收益: {miningStats.dailyRewards.toFixed(2)} ZONE</p>
                </div>
              </div>

              <div className="bg-gray-800 p-4 rounded-lg">
                <h2 className="text-lg font-bold mb-4">我的 NFT</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {nfts.map(nft => (
                    <div key={nft.id} className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex justify-between items-center mb-2">
                        <span className="text-binance-yellow font-bold">{nft.type}</span>
                        <span className="text-sm text-gray-400">#{nft.id}</span>
                      </div>
                      <div className="space-y-1">
                        <p className="text-sm">基础算力: {nft.power}</p>
                        <p className="text-sm">属性加成: +{nft.bonus}%</p>
                        <p className="text-xs text-gray-500">日收益: {(nft.power * DAILY_REWARD_RATE).toFixed(2)} ZONE</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="bg-gray-800 p-4 rounded-lg">
                <h2 className="text-lg font-bold mb-4">挖矿状态</h2>
                <div className="w-full bg-gray-700 rounded-full h-4">
                  <div
                    className="bg-binance-yellow h-4 rounded-full transition-all duration-500"
                    style={{ width: `${(miningStats.personalPower / miningStats.totalPower * 100).toFixed(2)}%` }}
                  ></div>
                </div>
                <p className="text-sm text-gray-400 mt-2">
                  占全网算力: {(miningStats.personalPower / miningStats.totalPower * 100).toFixed(2)}%
                </p>
                <div className="mt-4 text-sm text-gray-400">
                  <p>当前算力区间: {miningStats.personalPower.toLocaleString()} 
                    ({POWER_TIERS.find(t => miningStats.personalPower >= t.min && miningStats.personalPower <= t.max)?.bonus * 100}% 加成)</p>
                  <p>团队人数要求: {POWER_TIERS.find(t => miningStats.personalPower >= t.min && miningStats.personalPower <= t.max)?.requiredPeople}人</p>
                </div>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default NFTMining;
