import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWeb3React } from '@web3-react/core';
import { injected, setupNetwork } from '../utils/connectors';

const Navbar = () => {
  const { active, account, activate, deactivate } = useWeb3React();
  const location = useLocation();
  const [connecting, setConnecting] = useState(false);

  useEffect(() => {
    const connectWalletOnPageLoad = async () => {
      if (window.ethereum && window.ethereum.isConnected() && localStorage.getItem('previouslyConnected') === 'true') {
        try {
          await activate(injected);
        } catch (error) {
          console.error('Error on auto connect:', error);
        }
      }
    };
    connectWalletOnPageLoad();
  }, [activate]);

  const connectWallet = async () => {
    if (connecting) return;
    setConnecting(true);

    try {
      if (!window.ethereum) {
        alert('请安装MetaMask或其他Web3钱包');
        return;
      }

      // First request account access
      await window.ethereum.request({
        method: 'eth_requestAccounts'
      });

      // Then setup network and activate
      await setupNetwork();
      await activate(injected);
      localStorage.setItem('previouslyConnected', 'true');
    } catch (error) {
      console.error('Error connecting wallet:', error);
      if (error.name === 'UnsupportedChainIdError') {
        const hasSetup = await setupNetwork();
        if (hasSetup) {
          await activate(injected);
        }
      }
    } finally {
      setConnecting(false);
    }
  };

  const disconnectWallet = () => {
    try {
      deactivate();
      localStorage.removeItem('previouslyConnected');
    } catch (error) {
      console.error('Error disconnecting wallet:', error);
    }
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  const navItems = [
    { path: '/', label: '首页' },
    { path: '/ido', label: 'IDO' },
    { path: '/mystery-box', label: '盲盒' },
    { path: '/mining', label: '挖矿' },
    { path: '/market', label: '市场' },
  ];

  return (
    <nav className="bg-[#0B1120]/80 backdrop-blur-md border-b border-green-500/20 fixed w-full z-50">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <Link to="/" className="flex items-center">
            <span className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600">
              ZERO
            </span>
          </Link>

          {/* Navigation Links */}
          <div className="flex space-x-1">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200 ${
                  isActive(item.path)
                    ? 'bg-green-500/10 text-green-400'
                    : 'text-gray-400 hover:text-green-400 hover:bg-green-500/5'
                }`}
              >
                {item.label}
              </Link>
            ))}
          </div>

          {/* Connect Wallet Button */}
          {active ? (
            <div className="flex items-center">
              <span className="text-sm text-gray-300 mr-4">
                {account.slice(0, 6)}...{account.slice(-4)}
              </span>
              <button
                onClick={disconnectWallet}
                className="bg-red-600 text-white text-sm font-medium py-2 px-4 rounded-lg hover:bg-red-700"
              >
                断开连接
              </button>
            </div>
          ) : (
            <button
              onClick={connectWallet}
              disabled={connecting}
              className={`bg-gradient-to-r from-green-500 to-green-600 text-white text-sm font-medium py-2 px-4 rounded-lg hover:from-green-600 hover:to-green-700 transition-all ${
                connecting ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {connecting ? '连接中...' : '连接钱包'}
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
