import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWeb3React } from '@web3-react/core';
import { injected, setupNetwork } from '../utils/connectors';

const Navbar = () => {
  const { active, account, activate, deactivate } = useWeb3React();
  const location = useLocation();
  const [connecting, setConnecting] = useState(false);

  useEffect(() => {
    const connectWalletOnPageLoad = async () => {
      if (window.ethereum && window.ethereum.isConnected() && localStorage.getItem('previouslyConnected') === 'true') {
        try {
          await activate(injected);
        } catch (error) {
          console.error('Error on auto connect:', error);
        }
      }
    };
    connectWalletOnPageLoad();
  }, [activate]);

  const connectWallet = async () => {
    if (connecting) return;
    setConnecting(true);

    try {
      if (!window.ethereum) {
        alert('请安装MetaMask或其他Web3钱包');
        return;
      }

      await setupNetwork();
      await activate(injected);
      localStorage.setItem('previouslyConnected', 'true');
    } catch (error) {
      console.error('Error connecting wallet:', error);
      if (error.name === 'UnsupportedChainIdError') {
        const hasSetup = await setupNetwork();
        if (hasSetup) {
          await activate(injected);
        }
      }
    } finally {
      setConnecting(false);
    }
  };

  const disconnectWallet = async () => {
    try {
      deactivate();
      localStorage.removeItem('previouslyConnected');
    } catch (error) {
      console.error('Error disconnecting wallet:', error);
    }
  };

  return (
    <nav className="bg-binance-dark border-b border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex">
            <div className="flex-shrink-0 flex items-center">
              <span className="text-binance-yellow text-xl font-bold">AIMiner</span>
            </div>
            <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
              <Link
                to="/"
                className={`${
                  location.pathname === '/'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                IDO
              </Link>
              <Link
                to="/mystery-box"
                className={`${
                  location.pathname === '/mystery-box'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                宝箱
              </Link>
              <Link
                to="/mining"
                className={`${
                  location.pathname === '/mining'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                挖矿
              </Link>
              <Link
                to="/market"
                className={`${
                  location.pathname === '/market'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                市场
              </Link>
            </div>
          </div>
          <div className="flex items-center">
            {!active ? (
              <button
                onClick={connectWallet}
                disabled={connecting}
                className="bg-binance-yellow text-binance-dark px-4 py-2 rounded-lg text-sm font-medium hover:opacity-90 transition-opacity disabled:opacity-50"
              >
                {connecting ? '连接中...' : '连接钱包'}
              </button>
            ) : (
              <div className="flex items-center space-x-4">
                <div className="text-gray-300 text-sm">
                  {account.slice(0, 6)}...{account.slice(-4)}
                </div>
                <button
                  onClick={disconnectWallet}
                  className="text-gray-400 hover:text-gray-300 text-sm"
                >
                  断开连接
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
