import React from 'react';
import PropTypes from 'prop-types';
import { formatDistanceToNow } from 'date-fns';
import { zhCN } from 'date-fns/locale';
import { ethers } from 'ethers';

const NFTHistory = ({ 
  history, 
  loading = false,
  className = '' 
}) => {
  if (loading) {
    return (
      <div className={`space-y-4 ${className}`}>
        {[...Array(3)].map((_, index) => (
          <div key={index} className="animate-pulse">
            <div className="bg-[#1e2839] p-4 rounded-lg">
              <div className="h-4 bg-gray-700/30 rounded w-3/4 mb-2"></div>
              <div className="h-4 bg-gray-700/30 rounded w-1/2"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (!history || history.length === 0) {
    return (
      <div className={`text-center py-8 text-gray-400 ${className}`}>
        暂无交易记录
      </div>
    );
  }

  return (
    <div className={`space-y-4 ${className}`}>
      {history.map((record, index) => (
        <div key={index} className="bg-[#1e2839] p-4 rounded-lg">
          <div className="flex justify-between items-start">
            <div>
              <p className="text-sm text-gray-400">
                {record.type === 'buy' ? '购买' : '出售'} NFT #{record.tokenId}
              </p>
              <p className="mt-1">
                <span className="text-sm text-gray-400">价格：</span>
                <span className="font-bold">{ethers.utils.formatEther(record.price)} ETH</span>
              </p>
            </div>
            <p className="text-sm text-gray-400">
              {formatDistanceToNow(new Date(record.timestamp * 1000), {
                addSuffix: true,
                locale: zhCN
              })}
            </p>
          </div>
          <div className="mt-2 text-sm">
            <span className="text-gray-400">
              {record.type === 'buy' ? '卖家' : '买家'}：
            </span>
            <span className="font-mono">
              {record.type === 'buy' ? record.seller : record.buyer}
            </span>
          </div>
        </div>
      ))}
    </div>
  );
};

NFTHistory.propTypes = {
  history: PropTypes.arrayOf(
    PropTypes.shape({
      tokenId: PropTypes.number.isRequired,
      type: PropTypes.oneOf(['buy', 'sell']).isRequired,
      price: PropTypes.string.isRequired,
      seller: PropTypes.string.isRequired,
      buyer: PropTypes.string.isRequired,
      timestamp: PropTypes.number.isRequired
    })
  ).isRequired,
  loading: PropTypes.bool,
  className: PropTypes.string
};

export default React.memo(NFTHistory);
