import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const [userContribution, setUserContribution] = useState(0); // 添加用户贡献记录
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  const idoInfo = {
    totalSupply: 100000000,
    privateSale: {
      amount: 10000000,
      bnbTarget: 769.23,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 15
    },
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00'
  };

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.privateSale.minContribution || bnbAmount > idoInfo.privateSale.maxContribution) {
      alert(`请输入${idoInfo.privateSale.minContribution}-${idoInfo.privateSale.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
      // 交易成功后更新用户贡献
      setUserContribution(prev => prev + bnbAmount);
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-black py-12 px-4">
      <div className="max-w-4xl mx-auto">
        {/* IDO Banner */}
        <div className="text-center mb-12">
          <h1 className="text-5xl font-bold mb-4 text-yellow-400">
            AIMiner (DPAP) IDO
          </h1>
          <p className="text-2xl text-yellow-400 font-bold mb-4">总量: 1亿 DPAP</p>
          <div className="inline-flex items-center px-6 py-3 rounded-full bg-gray-800/50 border border-yellow-400/20">
            <span className="text-yellow-400 mr-3 text-xl">⏰</span>
            <p className="text-gray-300 text-lg">
              {idoInfo.startTime} - {idoInfo.endTime}
            </p>
          </div>
        </div>

        {/* 主要内容区域 */}
        <div className="bg-gray-800/30 backdrop-blur rounded-2xl p-8">
          <div className="grid gap-8">
            {/* 用户参与信息 */}
            {active && userContribution > 0 && (
              <div className="bg-yellow-400/20 rounded-xl p-6 border border-yellow-400/30">
                <h3 className="text-yellow-400 text-xl font-bold mb-4">我的参与</h3>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <p className="text-gray-400 mb-2">已参与</p>
                    <p className="text-yellow-400 text-2xl font-bold">{userContribution} BNB</p>
                  </div>
                  <div>
                    <p className="text-gray-400 mb-2">预计获得</p>
                    <p className="text-yellow-400 text-2xl font-bold">{(userContribution * idoInfo.privateSale.dpapPerBNB).toLocaleString()} DPAP</p>
                  </div>
                </div>
              </div>
            )}

            {/* 私募信息 */}
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">发行总量</p>
                <p className="text-yellow-400 text-xl font-bold">1亿 DPAP</p>
              </div>
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">私募总量</p>
                <p className="text-yellow-400 text-xl font-bold">1000万 DPAP</p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">募资目标</p>
                <p className="text-yellow-400 text-xl font-bold">769.23 BNB</p>
              </div>
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">兑换比例</p>
                <p className="text-yellow-400 text-xl font-bold">1 BNB = 13,000 DPAP</p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">私募价格</p>
                <p className="text-yellow-400 text-xl font-bold">≈ 0.05U</p>
              </div>
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">最大购买</p>
                <p className="text-yellow-400 text-lg font-bold">15 BNB</p>
                <p className="text-gray-400 text-sm">= 195,000 DPAP</p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">最小购买</p>
                <p className="text-yellow-400 text-lg font-bold">0.1 BNB</p>
                <p className="text-gray-400 text-sm">= 1,300 DPAP</p>
              </div>
              <div className="bg-gray-800/50 rounded-xl p-6">
                <p className="text-gray-400 mb-2">开始时间</p>
                <p className="text-yellow-400 text-lg font-bold">2025-02-10 20:00:00</p>
              </div>
            </div>

            {/* 参与表单 */}
            <div className="mt-4">
              {!active ? (
                <button
                  onClick={connectWallet}
                  className="w-full bg-yellow-400 text-black font-bold py-4 px-6 rounded-xl hover:bg-yellow-500 transition-all"
                >
                  连接钱包
                </button>
              ) : (
                <div className="space-y-6">
                  <div>
                    <label className="block text-gray-400 mb-2">
                      参与数量 (BNB)
                    </label>
                    <div className="relative">
                      <input
                        type="number"
                        min={idoInfo.privateSale.minContribution}
                        max={idoInfo.privateSale.maxContribution}
                        step="0.1"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        className="w-full bg-gray-800/50 border border-gray-700 rounded-xl px-4 py-4 text-white text-lg focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
                        placeholder="输入参与数量"
                      />
                      <span className="absolute right-4 top-4 text-gray-400">BNB</span>
                    </div>
                    <div className="mt-4 p-4 bg-yellow-400/10 rounded-xl">
                      <p className="text-yellow-400">
                        预计获得：{amount ? (amount * idoInfo.privateSale.dpapPerBNB).toLocaleString() : '0'} DPAP
                      </p>
                    </div>
                  </div>

                  <button
                    onClick={handleContribute}
                    className="w-full bg-yellow-400 text-black font-bold py-4 px-6 rounded-xl hover:bg-yellow-500 transition-all"
                  >
                    确认参与
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IDO;
