import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Home = () => {
  const navigate = useNavigate();
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [cursorVariant, setCursorVariant] = useState("default");

  // 监听鼠标移动
  useEffect(() => {
    const mouseMove = (e) => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY
      });
    };

    window.addEventListener("mousemove", mouseMove);

    return () => {
      window.removeEventListener("mousemove", mouseMove);
    };
  }, []);

  // 自定义光标动画
  const variants = {
    default: {
      x: mousePosition.x - 16,
      y: mousePosition.y - 16,
      opacity: 0.5,
    },
    hover: {
      height: 96,
      width: 96,
      x: mousePosition.x - 48,
      y: mousePosition.y - 48,
      opacity: 0.3,
      background: "linear-gradient(to right, #4ade80, #3b82f6)",
      transition: {
        type: "spring",
        mass: 0.6
      }
    }
  };

  // 使用 Intersection Observer 监听元素是否在视图中
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  // 动画配置
  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        staggerChildren: 0.1
      }
    }
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 自定义光标 */}
      <motion.div
        className="fixed top-0 left-0 w-8 h-8 bg-green-500 rounded-full pointer-events-none z-50 mix-blend-screen"
        variants={variants}
        animate={cursorVariant}
      />

      {/* 背景动画效果 */}
      <div className="absolute inset-0">
        <div className="absolute inset-0">
          {/* 网格线 */}
          <div className="absolute inset-0 grid grid-cols-12 grid-rows-8">
            {[...Array(96)].map((_, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0 }}
                animate={{ opacity: 0.05 }}
                transition={{ duration: 1, delay: i * 0.01 }}
                className="border-[0.5px] border-green-500/5"
              />
            ))}
          </div>
          {/* 渐变背景 */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 2 }}
            className="absolute inset-0 bg-gradient-to-b from-green-500/5 via-transparent to-green-500/5"
          />
          {/* 动态光点 */}
          <div className="absolute inset-0">
            {[...Array(20)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute w-2 h-2 bg-green-500 rounded-full"
                initial={{
                  x: Math.random() * window.innerWidth,
                  y: Math.random() * window.innerHeight,
                  scale: 0
                }}
                animate={{
                  x: Math.random() * window.innerWidth,
                  y: Math.random() * window.innerHeight,
                  scale: [0, 1, 0],
                }}
                transition={{
                  duration: Math.random() * 3 + 2,
                  repeat: Infinity,
                  repeatType: "reverse"
                }}
              />
            ))}
          </div>
        </div>
      </div>

      {/* 主要内容 */}
      <div className="relative container mx-auto px-4 py-16">
        {/* Hero Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="relative pt-20 pb-20 text-center"
        >
          <div className="max-w-4xl mx-auto px-4">
            <motion.h1
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="text-5xl font-bold bg-gradient-to-r from-green-400 to-blue-500 bg-clip-text text-transparent mb-6"
              onMouseEnter={() => setCursorVariant("hover")}
              onMouseLeave={() => setCursorVariant("default")}
            >
              ZERO
            </motion.h1>
            <motion.p
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              className="text-xl text-gray-400 mb-12 leading-relaxed max-w-2xl mx-auto"
            >
              基于人工智能的数字货币挖矿解决方案，为您提供智能、高效、安全的挖矿体验
            </motion.p>
            <motion.div
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.6 }}
              className="flex justify-center gap-6"
            >
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => navigate('/mystery-box')}
                className="px-8 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors font-medium"
                onMouseEnter={() => setCursorVariant("hover")}
                onMouseLeave={() => setCursorVariant("default")}
              >
                立即开始
              </motion.button>
            </motion.div>
          </div>
        </motion.div>

        {/* 数据统计 */}
        <motion.div
          ref={ref}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          variants={containerVariants}
          className="mb-20"
        >
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {[
              { value: "12,580", label: "总算力" },
              { value: "5,280", label: "活跃矿工" },
              { value: "1.2M", label: "已挖出代币" },
              { value: "$2.8M", label: "总市值" }
            ].map((stat, index) => (
              <motion.div
                key={index}
                variants={{
                  hidden: { opacity: 0, y: 20 },
                  visible: { opacity: 1, y: 0 }
                }}
                whileHover={{ scale: 1.05 }}
                className="bg-[#1e2839] p-6 rounded-xl text-center transform transition-transform border border-green-500/20"
                onMouseEnter={() => setCursorVariant("hover")}
                onMouseLeave={() => setCursorVariant("default")}
              >
                <motion.div
                  initial={{ opacity: 0, scale: 0.5 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="text-3xl md:text-4xl font-bold text-green-400 mb-2"
                >
                  {stat.value}
                </motion.div>
                <div className="text-green-400/60">{stat.label}</div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* 核心特点 */}
        <motion.div
          ref={ref}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          variants={containerVariants}
          className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16"
        >
          {[
            {
              icon: "🎯",
              title: "AI 智能合约",
              description: "智能合约自动执行，交易安全透明，收益实时结算"
            },
            {
              icon: "🎁",
              title: "神秘盲盒系统",
              description: "四种稀有度 NFT，独特外观设计，特殊属性加成"
            },
            {
              icon: "💎",
              title: "NFT 交易市场",
              description: "自由交易 NFT，实时市场行情，安全交易保障"
            }
          ].map((feature, index) => (
            <motion.div
              key={index}
              variants={{
                hidden: { opacity: 0, x: -20 },
                visible: { opacity: 1, x: 0 }
              }}
              whileHover={{ scale: 1.05 }}
              className="bg-[#1e2839] p-6 rounded-lg"
              onMouseEnter={() => setCursorVariant("hover")}
              onMouseLeave={() => setCursorVariant("default")}
            >
              <div className="text-yellow-500 text-4xl mb-4">{feature.icon}</div>
              <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
              <p className="text-gray-400">{feature.description}</p>
            </motion.div>
          ))}
        </motion.div>

        {/* NFT 等级介绍 */}
        <motion.div
          ref={ref}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          variants={containerVariants}
          className="mb-16"
        >
          <h2 className="text-3xl font-bold mb-8 text-center">NFT 等级系统</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {[
              {
                name: "普通 NFT",
                level: "N",
                color: "text-white",
                probability: "55%",
                pity: "1",
                guarantee: "无特殊保底"
              },
              {
                name: "稀有 NFT",
                level: "R",
                color: "text-blue-400",
                probability: "15%",
                pity: "1",
                guarantee: "无特殊保底"
              },
              {
                name: "超稀有 NFT",
                level: "SR",
                color: "text-purple-400",
                probability: "5%",
                pity: "1",
                guarantee: "每100次必得SR级"
              },
              {
                name: "传说 NFT",
                level: "SSR",
                color: "text-yellow-400",
                probability: "1%",
                pity: "1",
                guarantee: "每300次必得SSR"
              }
            ].map((nft, index) => (
              <motion.div
                key={index}
                variants={{
                  hidden: { opacity: 0, scale: 0.8 },
                  visible: { opacity: 1, scale: 1 }
                }}
                whileHover={{ scale: 1.05 }}
                className="bg-[#1e2839] p-6 rounded-lg"
                onMouseEnter={() => setCursorVariant("hover")}
                onMouseLeave={() => setCursorVariant("default")}
              >
                <div className="flex justify-between items-center mb-4">
                  <span className={nft.color}>{nft.name}</span>
                  <span className={`${nft.color} font-bold`}>{nft.level}</span>
                </div>
                <ul className="text-gray-400 space-y-2">
                  <li>- 抽取概率: {nft.probability}</li>
                  <li>- Pity 值: {nft.pity}</li>
                  <li>- {nft.guarantee}</li>
                </ul>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* 挖矿收益说明 */}
        <motion.div
          ref={ref}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          variants={containerVariants}
          className="max-w-6xl mx-auto px-4 py-16"
        >
          <h2 className="text-3xl font-bold text-center mb-12">挖矿收益说明</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                title: "基础收益",
                items: [
                  "NFT 等级决定算力",
                  "每日固定收益发放",
                  "收益与算力成正比"
                ]
              },
              {
                title: "质押机制",
                items: [
                  "质押即可挖矿",
                  "不可解除质押",
                  "无锁定期限制"
                ]
              },
              {
                title: "收益加成",
                items: [
                  "NFT 特殊属性加成",
                  "质押时长加成",
                  "活动期间额外加成"
                ]
              }
            ].map((section, index) => (
              <motion.div
                key={index}
                variants={{
                  hidden: { opacity: 0, y: 20 },
                  visible: { opacity: 1, y: 0 }
                }}
                whileHover={{ scale: 1.05 }}
                className="bg-[#1A2438]/80 backdrop-blur-xl rounded-2xl p-8"
                onMouseEnter={() => setCursorVariant("hover")}
                onMouseLeave={() => setCursorVariant("default")}
              >
                <div>
                  <h3 className="text-xl font-bold mb-4">{section.title}</h3>
                  <div className="space-y-4">
                    {section.items.map((item, itemIndex) => (
                      <div key={itemIndex} className="flex items-center">
                        <div className="w-2 h-2 bg-gray-400 rounded-full mr-4"></div>
                        <span className="text-gray-400">{item}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* 路线图 */}
        <motion.div
          ref={ref}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          variants={containerVariants}
          className="mb-16"
        >
          <h2 className="text-3xl font-bold text-center mb-12">项目路线图</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            {[
              {
                phase: "1",
                title: "第一阶段",
                items: [
                  "项目启动",
                  "NFT 系统开发",
                  "智能合约部署"
                ]
              },
              {
                phase: "2",
                title: "第二阶段",
                items: [
                  "盲盒系统上线",
                  "NFT 交易市场",
                  "社区建设"
                ]
              },
              {
                phase: "3",
                title: "第三阶段",
                items: [
                  "AI 挖矿系统",
                  "质押机制优化",
                  "生态扩展"
                ]
              },
              {
                phase: "4",
                title: "第四阶段",
                items: [
                  "跨链整合",
                  "DAO 治理",
                  "全球扩张"
                ]
              }
            ].map((phase, index) => (
              <motion.div
                key={index}
                variants={{
                  hidden: { opacity: 0, scale: 0.8 },
                  visible: { opacity: 1, scale: 1 }
                }}
                whileHover={{ scale: 1.05 }}
                className="bg-[#1e2839] p-6 rounded-lg relative"
                onMouseEnter={() => setCursorVariant("hover")}
                onMouseLeave={() => setCursorVariant("default")}
              >
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ delay: index * 0.1 }}
                  className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-8 h-8 bg-green-500 rounded-full flex items-center justify-center text-white font-bold"
                >
                  {phase.phase}
                </motion.div>
                <h3 className="text-xl font-bold mb-4 mt-4">{phase.title}</h3>
                <ul className="text-gray-400 space-y-2">
                  {phase.items.map((item, itemIndex) => (
                    <motion.li
                      key={itemIndex}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 + itemIndex * 0.1 }}
                    >
                      - {item}
                    </motion.li>
                  ))}
                </ul>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* 合作伙伴 */}
        <motion.div
          ref={ref}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          variants={containerVariants}
          className="mb-16"
        >
          <h2 className="text-3xl font-bold text-center mb-12">合作伙伴</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
            {[1, 2, 3, 4].map((partner) => (
              <motion.div
                key={partner}
                variants={{
                  hidden: { opacity: 0, scale: 0.8 },
                  visible: { opacity: 1, scale: 1 }
                }}
                whileHover={{ scale: 1.05 }}
                className="bg-[#1e2839] p-6 rounded-lg flex items-center justify-center"
                onMouseEnter={() => setCursorVariant("hover")}
                onMouseLeave={() => setCursorVariant("default")}
              >
                <div className="w-24 h-24 bg-gray-700 rounded-lg flex items-center justify-center">
                  <span className="text-gray-400">Partner {partner}</span>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* 底部信息 */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1 }}
          className="text-center text-gray-400 py-8"
        >
          <p> 2025 ZERO. All rights reserved.</p>
        </motion.div>
      </div>
    </div>
  );
};

export default Home;
