import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';
import { ethers } from 'ethers';
import { useNavigate } from 'react-router-dom';

// 合约地址和ABI
const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";
const ZONE_TOKEN_ADDRESS = "YOUR_ZONE_TOKEN_ADDRESS";

const MYSTERY_BOX_ABI = [
  "function getMarketListing(uint256 tokenId) external view returns (tuple(address seller, uint256 price, bool isActive))",
  "function getNFTAttributes(uint256 tokenId) external view returns (tuple(uint8 rarity, uint256 power, uint256 dailyReward, uint256 maxReward, uint256 minedAmount, bool isStaked, uint256 stakeTime))",
  "function getOwnedNFTs(address user) external view returns (uint256[])",
  "function listNFT(uint256 tokenId, uint256 price) external",
  "function unlistNFT(uint256 tokenId) external",
  "function buyNFT(uint256 tokenId) external payable",
  "function stakeNFT(uint256 tokenId) external",
  "function approve(address to, uint256 tokenId) external",
  "function isApprovedForAll(address owner, address operator) external view returns (bool)",
  "function setApprovalForAll(address operator, bool approved) external"
];

const ZONE_TOKEN_ABI = [
  "function balanceOf(address account) external view returns (uint256)",
  "function allowance(address owner, address spender) external view returns (uint256)",
  "function approve(address spender, uint256 amount) external returns (bool)"
];

const NFT_RARITY = ['N', 'R', 'SR', 'SSR'];

const NFTMarket = () => {
  const { active, account, activate, library } = useWeb3React();
  const navigate = useNavigate();
  const [marketItems, setMarketItems] = useState([]);
  const [myNFTs, setMyNFTs] = useState([]);
  const [selectedTab, setSelectedTab] = useState('market');
  const [isLoading, setIsLoading] = useState(true);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const loadMarketData = async () => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());

      // 加载我的NFT
      const myTokenIds = await contract.getOwnedNFTs(account);
      const myNFTsData = await Promise.all(
        myTokenIds.map(async (tokenId) => {
          const attributes = await contract.getNFTAttributes(tokenId);
          const listing = await contract.getMarketListing(tokenId);

          // 如果已质押，跳过
          if (attributes.isStaked) return null;

          return {
            id: tokenId.toString(),
            type: NFT_RARITY[attributes.rarity],
            power: attributes.power.toString(),
            dailyReward: ethers.utils.formatEther(attributes.dailyReward),
            maxReward: ethers.utils.formatEther(attributes.maxReward),
            listed: listing.isActive,
            price: listing.isActive ? ethers.utils.formatEther(listing.price) : null
          };
        })
      );
      setMyNFTs(myNFTsData.filter(nft => nft !== null));

      // 加载市场列表
      // 这里需要一个获取所有市场挂单的方法，暂时使用模拟数据
      const marketListings = []; // 这里应该从合约获取所有活跃的挂单
      const marketItemsData = await Promise.all(
        marketListings.map(async (tokenId) => {
          const attributes = await contract.getNFTAttributes(tokenId);
          const listing = await contract.getMarketListing(tokenId);
          return {
            id: tokenId.toString(),
            type: NFT_RARITY[attributes.rarity],
            power: attributes.power.toString(),
            dailyReward: ethers.utils.formatEther(attributes.dailyReward),
            maxReward: ethers.utils.formatEther(attributes.maxReward),
            price: ethers.utils.formatEther(listing.price),
            seller: listing.seller
          };
        })
      );
      setMarketItems(marketItemsData);

      setIsLoading(false);
    } catch (error) {
      console.error('Error loading market data:', error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (active) {
      loadMarketData();
    }
  }, [active, account]);

  const handleBuy = async (nftId) => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const listing = await contract.getMarketListing(nftId);

      const tx = await contract.buyNFT(nftId, {
        value: listing.price
      });
      await tx.wait();

      alert('购买成功！');
      loadMarketData();
    } catch (error) {
      console.error('Error buying NFT:', error);
      alert('购买失败: ' + error.message);
    }
  };

  const handleList = async (nftId) => {
    try {
      const price = prompt('请输入上架价格 (BNB):');
      if (!price) return;

      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());

      // 检查NFT授权
      const isApproved = await contract.isApprovedForAll(account, MYSTERY_BOX_ADDRESS);
      if (!isApproved) {
        const approveTx = await contract.setApprovalForAll(MYSTERY_BOX_ADDRESS, true);
        await approveTx.wait();
      }

      const priceWei = ethers.utils.parseEther(price);
      const tx = await contract.listNFT(nftId, priceWei);
      await tx.wait();

      alert('上架成功！');
      loadMarketData();
    } catch (error) {
      console.error('Error listing NFT:', error);
      alert('上架失败: ' + error.message);
    }
  };

  const handleDelist = async (nftId) => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await contract.unlistNFT(nftId);
      await tx.wait();

      alert('下架成功！');
      loadMarketData();
    } catch (error) {
      console.error('Error delisting NFT:', error);
      alert('下架失败: ' + error.message);
    }
  };

  const handleStake = async (nftId) => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await contract.stakeNFT(nftId);
      await tx.wait();

      alert('质押成功！');
      navigate('/mining'); // 直接跳转到挖矿页面
    } catch (error) {
      console.error('Error staking NFT:', error);
      alert('质押失败: ' + error.message);
    }
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 背景动画效果 */}
      <div className="absolute inset-0">
        {/* 基础网格背景 */}
        <div className="absolute inset-0">
          <div className="absolute inset-0 grid grid-cols-12 grid-rows-8">
            {[...Array(96)].map((_, i) => (
              <div key={i} className="border-[0.5px] border-green-500/5 relative">
                {Math.random() > 0.9 && (
                  <div className="absolute inset-0 bg-green-500/10 animate-pulse"></div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* 动态电路线 */}
        <div className="absolute inset-0">
          {[...Array(8)].map((_, i) => (
            <div
              key={`circuit-${i}`}
              className="absolute h-[1px] bg-gradient-to-r from-transparent via-green-500/30 to-transparent"
              style={{
                width: '40%',
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                transform: `rotate(${Math.random() * 360}deg)`,
                animation: `circuitFlow 4s infinite linear`,
                animationDelay: `${i * 0.5}s`
              }}
            />
          ))}
        </div>

        {/* 光晕效果 */}
        <div className="absolute inset-0">
          <div className="absolute top-0 -left-1/4 w-1/2 h-1/2 bg-green-500/5 blur-[100px] animate-pulse"></div>
          <div className="absolute bottom-0 -right-1/4 w-1/2 h-1/2 bg-green-500/5 blur-[100px] animate-pulse delay-1000"></div>
        </div>

        {/* 数据流动效果 */}
        <div className="absolute inset-0">
          {[...Array(15)].map((_, i) => (
            <div
              key={`data-${i}`}
              className="absolute w-[2px] bg-gradient-to-b from-transparent via-green-500/20 to-transparent"
              style={{
                height: '30%',
                left: `${(i * 7)}%`,
                top: '-30%',
                animation: `dataStream 3s infinite linear`,
                animationDelay: `${i * 0.2}s`
              }}
            />
          ))}
        </div>

        {/* 扫描线效果 */}
        <div 
          className="absolute inset-0 bg-gradient-to-b from-transparent via-green-500/5 to-transparent"
          style={{
            animation: 'scan 8s infinite linear',
            transform: 'translateY(-100%)'
          }}
        ></div>

        {/* 闪烁的节点 */}
        <div className="absolute inset-0">
          {[...Array(20)].map((_, i) => (
            <div
              key={`node-${i}`}
              className="absolute w-1 h-1 rounded-full bg-green-400/50"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animation: `nodeBlink 2s infinite ease-in-out`,
                animationDelay: `${Math.random() * 2}s`
              }}
            />
          ))}
        </div>
      </div>

      {/* 主要内容 */}
      <div className="relative container mx-auto px-4 py-8">
        <div className="text-center mb-12">
          <div className="relative inline-block">
            <div className="absolute -inset-2 bg-green-500/20 rounded-lg blur-lg"></div>
            <h1 className="relative text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600">
              NFT 交易市场
            </h1>
          </div>
        </div>

        {!active ? (
          <button
            onClick={connectWallet}
            className="w-full bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-3 px-8 rounded-lg hover:from-green-600 hover:to-green-700 transition-all"
          >
            连接钱包
          </button>
        ) : (
          <div className="space-y-6">
            <div className="flex space-x-4 border-b border-green-500/20">
              <button
                className={`px-4 py-2 ${
                  selectedTab === 'market'
                    ? 'text-green-400 border-b-2 border-green-400'
                    : 'text-gray-400'
                }`}
                onClick={() => setSelectedTab('market')}
              >
                市场列表
              </button>
              <button
                className={`px-4 py-2 ${
                  selectedTab === 'myNFTs'
                    ? 'text-green-400 border-b-2 border-green-400'
                    : 'text-gray-400'
                }`}
                onClick={() => setSelectedTab('myNFTs')}
              >
                我的NFT
              </button>
            </div>

            {isLoading ? (
              <div className="text-center py-8">加载中...</div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {selectedTab === 'market' && marketItems.map(item => (
                  <div key={item.id} className="bg-gray-800 p-4 rounded-lg">
                    <div className="flex justify-between items-center mb-4">
                      <span className="text-green-400 font-bold">{item.type}</span>
                      <span className="text-sm text-gray-400">#{item.id}</span>
                    </div>
                    <div className="space-y-2 mb-4">
                      <p className="text-sm">基础算力: {item.power}</p>
                      <p className="text-sm">日收益: {item.dailyReward} ZONE</p>
                      <p className="text-sm">最大收益: {item.maxReward} ZONE</p>
                      <p className="text-sm">卖家: {item.seller}</p>
                      <p className="text-lg font-bold text-green-400">
                        {item.price} BNB
                      </p>
                    </div>
                    <button
                      onClick={() => handleBuy(item.id)}
                      className="w-full bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-2 px-4 rounded-lg hover:from-green-600 hover:to-green-700 transition-all"
                    >
                      购买
                    </button>
                  </div>
                ))}

                {selectedTab === 'myNFTs' && myNFTs.map(nft => (
                  <div key={nft.id} className="bg-gray-800 p-4 rounded-lg">
                    <div className="flex justify-between items-center mb-4">
                      <span className="text-green-400 font-bold">{nft.type}</span>
                      <span className="text-sm text-gray-400">#{nft.id}</span>
                    </div>
                    <div className="space-y-2 mb-4">
                      <p className="text-sm">基础算力: {nft.power}</p>
                      <p className="text-sm">日收益: {nft.dailyReward} ZONE</p>
                      <p className="text-sm">最大收益: {nft.maxReward} ZONE</p>
                      {nft.listed && (
                        <p className="text-lg font-bold text-green-400">
                          当前价格: {nft.price} BNB
                        </p>
                      )}
                    </div>
                    <div className="space-y-2">
                      <button
                        onClick={() => handleStake(nft.id)}
                        className="w-full bg-green-500 text-white font-bold py-2 px-4 rounded-lg hover:opacity-90 transition-opacity"
                      >
                        质押挖矿
                      </button>
                      {!nft.listed && (
                        <button
                          onClick={() => handleList(nft.id)}
                          className="w-full bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-2 px-4 rounded-lg hover:from-green-600 hover:to-green-700 transition-all"
                        >
                          上架出售
                        </button>
                      )}
                      {nft.listed && (
                        <button
                          onClick={() => handleDelist(nft.id)}
                          className="w-full bg-red-500 text-white font-bold py-2 px-4 rounded-lg hover:opacity-90 transition-opacity"
                        >
                          下架
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {/* 添加动画关键帧 */}
      <style jsx>{`
        @keyframes circuitFlow {
          0% {
            opacity: 0;
            transform: translateX(-100%) rotate(${Math.random() * 360}deg);
          }
          50% {
            opacity: 1;
          }
          100% {
            opacity: 0;
            transform: translateX(100%) rotate(${Math.random() * 360}deg);
          }
        }

        @keyframes dataStream {
          0% {
            transform: translateY(-100%);
            opacity: 0;
          }
          50% {
            opacity: 1;
          }
          100% {
            transform: translateY(200%);
            opacity: 0;
          }
        }

        @keyframes scan {
          0% {
            transform: translateY(-100%);
          }
          100% {
            transform: translateY(100%);
          }
        }

        @keyframes nodeBlink {
          0%, 100% {
            opacity: 0.2;
            transform: scale(1);
          }
          50% {
            opacity: 1;
            transform: scale(1.5);
          }
        }

        .delay-1000 {
          animation-delay: 1000ms;
        }
      `}</style>
    </div>
  );
};

export default NFTMarket;
