import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';
import { ethers } from 'ethers';

const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";

const MYSTERY_BOX_ABI = [
  "function getNFTAttributes(uint256 tokenId) external view returns (tuple(uint8 rarity, uint256 power, uint256 dailyReward, uint256 maxReward, uint256 minedAmount, bool isStaked, uint256 stakeTime))",
  "function getStakedNFTs(address user) external view returns (uint256[])",
  "function calculateReward(uint256 tokenId) public view returns (uint256)",
  "function unstakeNFT(uint256 tokenId) external"
];

const NFT_RARITY = ['N', 'R', 'SR', 'SSR'];

const NFT_SETTINGS = {
  N: { power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MINING_LEVELS = [
  { name: 'LV1', min: 0, max: 10000, bonus: 0.10, requiredPeople: 5, bonusPerPerson: 0.05 },
  { name: 'LV2', min: 10001, max: 30000, bonus: 0.15, requiredPeople: 8, bonusPerPerson: 0.08 },
  { name: 'LV3', min: 30001, max: 100000, bonus: 0.20, requiredPeople: 12, bonusPerPerson: 0.12 },
  { name: 'LV4', min: 100001, max: 500000, bonus: 0.30, requiredPeople: 18, bonusPerPerson: 0.18 },
  { name: 'LV5', min: 500001, max: Infinity, bonus: 0.50, requiredPeople: 25, bonusPerPerson: 0.25 }
];

const NFT_PROBABILITIES = {
  N: 0.55,
  R: 0.15,
  SR: 0.05,
  SSR: 0.01
};

const NFTMining = () => {
  const { active, account, activate, library } = useWeb3React();
  const [miningStats, setMiningStats] = useState({
    totalPower: 0,
    personalPower: 0,
    efficiency: 0,
    currentRewards: 0,
    dailyRewards: 0,
    teamMembers: 0,
    miningLevel: 'LV1'
  });
  const [nfts, setNfts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const loadMiningData = async () => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());

      // 加载质押的NFT
      const stakedTokenIds = await contract.getStakedNFTs(account);
      const stakedNFTsData = await Promise.all(
        stakedTokenIds.map(async (tokenId) => {
          const attributes = await contract.getNFTAttributes(tokenId);
          const reward = await contract.calculateReward(tokenId);

          return {
            id: tokenId.toString(),
            type: NFT_RARITY[attributes.rarity],
            power: attributes.power.toString(),
            dailyReward: ethers.utils.formatEther(attributes.dailyReward),
            maxReward: ethers.utils.formatEther(attributes.maxReward),
            minedAmount: ethers.utils.formatEther(attributes.minedAmount),
            currentReward: ethers.utils.formatEther(reward),
            isStaked: attributes.isStaked
          };
        })
      );
      setNfts(stakedNFTsData);

      // 计算总算力和每日收益
      const totalPower = stakedNFTsData.reduce((sum, nft) => sum + parseInt(nft.power), 0);
      const dailyRewards = stakedNFTsData.reduce((sum, nft) => sum + parseFloat(nft.dailyReward), 0);

      setMiningStats(prev => ({
        ...prev,
        personalPower: totalPower,
        dailyRewards
      }));

      setIsLoading(false);
    } catch (error) {
      console.error('Error loading mining data:', error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (active) {
      loadMiningData();
    }
  }, [active, account]);

  const handleUnstake = async (nftId) => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await contract.unstakeNFT(nftId);
      await tx.wait();

      alert('解除质押成功！');
      loadMiningData();
    } catch (error) {
      console.error('Error unstaking NFT:', error);
      alert('解除质押失败: ' + error.message);
    }
  };

  const calculateMiningLevel = (power) => {
    const level = MINING_LEVELS.find(l => power >= l.min && power <= l.max);
    return level || MINING_LEVELS[0];
  };

  const calculateTeamBonus = (power, members) => {
    const level = calculateMiningLevel(power);
    return Math.min(members, level.requiredPeople) * level.bonusPerPerson;
  };

  const handleMining = () => {
    // 开始挖矿逻辑
  };

  const handleWithdraw = () => {
    // 领取收益逻辑
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 挖矿动画背景 */}
      <div className="absolute inset-0">
        {/* 电路背景 */}
        <div className="absolute inset-0">
          {/* 水平线 */}
          {[...Array(12)].map((_, i) => (
            <div
              key={`h-${i}`}
              className="absolute w-full h-[1px] bg-green-500/20"
              style={{ 
                top: `${i * 10}%`,
                transform: i % 2 === 0 ? 'translateX(-20%)' : 'translateX(20%)'
              }}
            >
              {/* 折线 */}
              <div 
                className="absolute right-[30%] w-[100px] h-[1px] bg-green-500/20"
                style={{
                  transform: `rotate(${i % 2 === 0 ? 45 : -45}deg)`,
                  transformOrigin: i % 2 === 0 ? 'right bottom' : 'right top'
                }}
              />
            </div>
          ))}

          {/* 垂直线 */}
          {[...Array(8)].map((_, i) => (
            <div
              key={`v-${i}`}
              className="absolute h-full w-[1px] bg-green-500/20"
              style={{ 
                left: `${i * 15}%`,
                transform: i % 2 === 0 ? 'translateY(-10%)' : 'translateY(10%)'
              }}
            >
              {/* 交叉点 */}
              {[...Array(4)].map((_, j) => (
                <div
                  key={`node-${i}-${j}`}
                  className="absolute w-1.5 h-1.5 bg-green-400/30 rounded-full"
                  style={{ 
                    top: `${j * 30}%`,
                    left: '-2px',
                    boxShadow: '0 0 4px #22c55e'
                  }}
                />
              ))}
            </div>
          ))}

          {/* 流动的粒子 */}
          {[...Array(6)].map((_, i) => (
            <div
              key={`particle-${i}`}
              className="absolute w-1 h-1 rounded-full bg-green-400/60"
              style={{
                left: `${Math.random() * 100}%`,
                top: '0',
                animation: `flowParticle 4s infinite linear`,
                animationDelay: `${i * 0.6}s`
              }}
            />
          ))}

          {/* 光束效果 */}
          {[...Array(3)].map((_, i) => (
            <div
              key={`beam-${i}`}
              className="absolute h-full w-[2px]"
              style={{
                left: `${30 + i * 20}%`,
                background: 'linear-gradient(to bottom, transparent, #22c55e20, transparent)',
                animation: `lightBeam 3s infinite ease-in-out`,
                animationDelay: `${i * 1}s`
              }}
            />
          ))}
        </div>

        {/* 矿机运行动画 */}
        <div className="absolute inset-0">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute w-1 h-16 bg-green-500/20"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animation: `mine ${Math.random() * 2 + 1}s infinite`,
                animationDelay: `${Math.random() * 2}s`
              }}
            />
          ))}
        </div>

        {/* 电路板背景 */}
        <div 
          className="absolute inset-0 opacity-10"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 50h20l10-10 10 10h20m20 0h20' stroke='%2330FF94' stroke-width='1' fill='none'/%3E%3Cpath d='M50 0v20l10 10-10 10v20m0 20v20' stroke='%2330FF94' stroke-width='1' fill='none'/%3E%3C/svg%3E")`,
            backgroundSize: '100px 100px'
          }}
        />

        {/* 能量波动 */}
        <div className="absolute inset-0">
          <div className="absolute top-0 left-0 w-full h-1/2 bg-gradient-to-b from-green-500/5 to-transparent"></div>
          <div className="absolute bottom-0 left-0 w-full h-1/2 bg-gradient-to-t from-green-500/5 to-transparent"></div>
        </div>
      </div>

      {/* 主要内容 */}
      <div className="relative container mx-auto px-4 py-8">
        {/* 标题区域 */}
        <div className="text-center mb-12">
          <div className="relative inline-block">
            <div className="absolute -inset-2 bg-green-500/20 rounded-lg blur-lg"></div>
            <h1 className="relative text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600">
              NFT 挖矿
            </h1>
          </div>
          <div className="mt-4 text-green-400/80">实时算力监控 · 智能收益分析</div>
        </div>

        {isLoading ? (
          <div className="flex items-center justify-center min-h-[400px]">
            <div className="animate-spin rounded-full h-12 w-12 border-4 border-green-500 border-t-transparent"></div>
          </div>
        ) : (
          <>
            {/* 挖矿状态展示 */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
              {/* NFT 状态卡片 */}
              <div className="relative group">
                <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
                <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-bold text-green-400">NFT 状态</h3>
                    <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse"></div>
                  </div>
                  <div className="space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">持有数量</span>
                      <span className="text-green-400 font-bold">{nfts.length}</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">总算力</span>
                      <span className="text-green-400 font-bold">{miningStats.personalPower} H/s</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">全网占比</span>
                      <span className="text-green-400 font-bold">{((miningStats.personalPower / miningStats.totalPower) * 100).toFixed(2)}%</span>
                    </div>
                  </div>
                </div>
              </div>

              {/* 收益统计卡片 */}
              <div className="relative group">
                <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
                <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-bold text-green-400">收益统计</h3>
                    <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse"></div>
                  </div>
                  <div className="space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">当前收益</span>
                      <span className="text-green-400 font-bold">{miningStats.currentRewards} ZONE</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">预计日收益</span>
                      <span className="text-green-400 font-bold">{miningStats.dailyRewards} ZONE</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">累计收益</span>
                      <span className="text-green-400 font-bold">{nfts.reduce((sum, nft) => sum + parseFloat(nft.currentReward), 0)} ZONE</span>
                    </div>
                  </div>
                </div>
              </div>

              {/* 运行状态卡片 */}
              <div className="relative group">
                <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
                <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-bold text-green-400">运行状态</h3>
                    <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse"></div>
                  </div>
                  <div className="space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">运行时间</span>
                      <span className="text-green-400 font-bold">24h 30m</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">网络延迟</span>
                      <span className="text-green-400 font-bold">30ms</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">成功率</span>
                      <span className="text-green-400 font-bold">99.9%</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* NFT列表 */}
            <div className="mb-8">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold text-green-400">我的 NFT</h2>
                <div className="flex items-center space-x-4">
                  <div className="flex items-center space-x-2">
                    <span className="text-green-400/80">运行矿机: </span>
                    <span className="text-green-400 font-bold">{nfts.filter(nft => nft.isStaked).length}/{nfts.length}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className="text-green-400/80">全网算力: </span>
                    <span className="text-green-400 font-bold">{miningStats.totalPower.toLocaleString()} H/s</span>
                  </div>
                </div>
              </div>

              {nfts.length === 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {/* 示例 NFT 卡片 */}
                  {['N', 'R', 'SR', 'SSR'].map((type, index) => (
                    <div key={type} className="relative group">
                      <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
                      <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
                        <div className="aspect-square mb-4 relative overflow-hidden rounded-lg">
                          <div className={`absolute inset-0 bg-gradient-to-br ${
                            type === 'SSR' ? 'from-yellow-400/20 to-yellow-600/20' :
                            type === 'SR' ? 'from-purple-400/20 to-purple-600/20' :
                            type === 'R' ? 'from-blue-400/20 to-blue-600/20' :
                            'from-green-400/20 to-green-600/20'
                          }`}></div>

                          {/* 示例动画效果 */}
                          <div className="absolute inset-0 flex items-center justify-center">
                            <div className="w-24 h-24 relative">
                              <div className="absolute inset-0 border-4 border-green-500/30 rounded-full animate-spin-slow"></div>
                              <div className="absolute inset-2 border-4 border-green-400/40 rounded-full animate-pulse"></div>
                              <div className="absolute inset-4 bg-green-500/20 rounded-full animate-ping"></div>
                            </div>
                          </div>
                        </div>

                        {/* NFT 信息 */}
                        <div className="space-y-2">
                          {/* 等级和类型 */}
                          <div className="flex justify-between items-center">
                            <div className="flex items-center space-x-2">
                              <span className={`px-2 py-0.5 rounded text-xs font-bold ${
                                type === 'SSR' ? 'bg-yellow-400/20 text-yellow-400' :
                                type === 'SR' ? 'bg-purple-400/20 text-purple-400' :
                                type === 'R' ? 'bg-blue-400/20 text-blue-400' :
                                'bg-green-400/20 text-green-400'
                              }`}>
                                {type}
                              </span>
                              <span className="text-green-400/60 text-sm">示例</span>
                            </div>
                            <div className="flex items-center">
                              <div className="w-2 h-2 rounded-full bg-gray-500 mr-2"></div>
                              <span className="text-sm text-green-400/60">未拥有</span>
                            </div>
                          </div>

                          {/* 属性列表 */}
                          <div className="space-y-1.5 text-sm">
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">算力</span>
                              <span className="text-green-400">{NFT_SETTINGS[type].power} H/s</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">日收益</span>
                              <span className="text-green-400">{NFT_SETTINGS[type].dailyReward} ZONE</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">最大收益</span>
                              <span className="text-green-400">{NFT_SETTINGS[type].maxReward} ZONE</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">投资回报率</span>
                              <span className="text-green-400">{NFT_SETTINGS[type].roi}天</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">年化收益</span>
                              <span className="text-green-400">{NFT_SETTINGS[type].yearReturn}%</span>
                            </div>
                          </div>

                          {/* 操作按钮 */}
                          <div className="pt-2">
                            <button
                              className="w-full bg-green-500/10 hover:bg-green-500/20 text-green-400 border border-green-500/20 rounded-lg py-2 px-4 transition-colors"
                              onClick={() => window.open('https://opensea.io', '_blank')}
                            >
                              购买 NFT
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {nfts.map((nft) => (
                    <div key={nft.id} className="relative group">
                      <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
                      <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
                        <div className="aspect-square mb-4 relative overflow-hidden rounded-lg">
                          <div className={`absolute inset-0 bg-gradient-to-br ${
                            nft.type === 'SSR' ? 'from-yellow-400/20 to-yellow-600/20' :
                            nft.type === 'SR' ? 'from-purple-400/20 to-purple-600/20' :
                            nft.type === 'R' ? 'from-blue-400/20 to-blue-600/20' :
                            'from-green-400/20 to-green-600/20'
                          }`}></div>
                          {/* 添加运行动画 */}
                          {nft.isStaked && (
                            <>
                              {/* CPU运行动画 */}
                              <div className="absolute inset-0 flex items-center justify-center">
                                <div className="w-24 h-24 relative">
                                  {/* 外圈旋转 */}
                                  <div className="absolute inset-0 border-4 border-green-500/30 rounded-full animate-spin-slow"></div>
                                  {/* 内圈脉冲 */}
                                  <div className="absolute inset-2 border-4 border-green-400/40 rounded-full animate-pulse"></div>
                                  {/* 中心点闪烁 */}
                                  <div className="absolute inset-4 bg-green-500/20 rounded-full animate-ping"></div>
                                </div>
                              </div>
                              {/* 数据流动画 */}
                              <div className="absolute inset-0">
                                {[...Array(8)].map((_, i) => (
                                  <div
                                    key={i}
                                    className="absolute w-0.5 h-8 bg-green-500/20"
                                    style={{
                                      left: `${Math.random() * 100}%`,
                                      top: `${Math.random() * 100}%`,
                                      animation: `dataStream ${Math.random() * 2 + 1}s infinite`,
                                      animationDelay: `${Math.random() * 2}s`
                                    }}
                                  ></div>
                                ))}
                              </div>
                              {/* 能量波纹 */}
                              <div className="absolute inset-0 flex items-center justify-center">
                                <div className="w-32 h-32 absolute">
                                  <div className="absolute inset-0 border-2 border-green-500/20 rounded-full animate-ripple"></div>
                                  <div className="absolute inset-0 border-2 border-green-500/20 rounded-full animate-ripple" style={{ animationDelay: '0.3s' }}></div>
                                  <div className="absolute inset-0 border-2 border-green-500/20 rounded-full animate-ripple" style={{ animationDelay: '0.6s' }}></div>
                                </div>
                              </div>
                            </>
                          )}
                          <div className="absolute inset-0 flex items-center justify-center">
                            <span className="text-4xl">🖥️</span>
                          </div>
                        </div>
                        <div className="space-y-2">
                          {/* 等级和类型 */}
                          <div className="flex justify-between items-center">
                            <div className="flex items-center space-x-2">
                              <span className={`px-2 py-0.5 rounded text-xs font-bold ${
                                nft.type === 'SSR' ? 'bg-yellow-400/20 text-yellow-400' :
                                nft.type === 'SR' ? 'bg-purple-400/20 text-purple-400' :
                                nft.type === 'R' ? 'bg-blue-400/20 text-blue-400' :
                                'bg-green-400/20 text-green-400'
                              }`}>
                                {nft.type}
                              </span>
                              <span className="text-green-400/60 text-sm">#{nft.id}</span>
                            </div>
                            <div className="flex items-center">
                              <div className={`w-2 h-2 rounded-full ${nft.isStaked ? 'bg-green-500 animate-pulse' : 'bg-red-500'} mr-2`}></div>
                              <span className="text-sm text-green-400/60">{nft.isStaked ? '运行中' : '未运行'}</span>
                            </div>
                          </div>

                          {/* 属性列表 */}
                          <div className="space-y-1.5 text-sm">
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">算力</span>
                              <span className="text-green-400">{nft.power} H/s</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">日收益</span>
                              <span className="text-green-400">{nft.dailyReward} ZONE</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">已挖取</span>
                              <span className="text-green-400">{nft.minedAmount} ZONE</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">当前收益</span>
                              <span className="text-green-400">{nft.currentReward} ZONE</span>
                            </div>
                            <div className="flex justify-between items-center">
                              <span className="text-green-400/60">最大收益</span>
                              <span className="text-green-400">{nft.maxReward} ZONE</span>
                            </div>
                          </div>

                          {/* 操作按钮 */}
                          <div className="pt-2">
                            {nft.isStaked ? (
                              <button
                                onClick={() => handleUnstake(nft.id)}
                                className="w-full bg-red-500/10 hover:bg-red-500/20 text-red-400 border border-red-500/20 rounded-lg py-2 px-4 transition-colors"
                              >
                                解除质押
                              </button>
                            ) : (
                              <button
                                className="w-full bg-green-500/10 hover:bg-green-500/20 text-green-400 border border-green-500/20 rounded-lg py-2 px-4 transition-colors"
                              >
                                质押挖矿
                              </button>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 操作按钮 */}
            <div className="flex justify-center gap-4">
              <button 
                onClick={handleMining}
                disabled={nfts.length === 0}
                className={`relative group overflow-hidden bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-3 px-8 rounded-lg transition-all ${
                  nfts.length === 0 ? 'opacity-50 cursor-not-allowed' : 'hover:from-green-600 hover:to-green-700'
                }`}
              >
                <span className="relative z-10">开始挖矿</span>
                <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/10 to-transparent transition-transform duration-1000"></div>
              </button>
              <button 
                onClick={handleWithdraw}
                disabled={miningStats.currentRewards <= 0}
                className={`relative group overflow-hidden bg-transparent text-green-500 border-2 border-green-500 font-bold py-3 px-8 rounded-lg transition-colors ${
                  miningStats.currentRewards <= 0 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-green-500 hover:text-white'
                }`}
              >
                <span className="relative z-10">领取收益</span>
                <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/10 to-transparent transition-transform duration-1000"></div>
              </button>
            </div>
          </>
        )}
      </div>

      {/* 添加样式 */}
      <style jsx>{`
        @keyframes mine {
          0% { transform: translateY(0) scale(1); opacity: 0; }
          50% { transform: translateY(100vh) scale(0.5); opacity: 1; }
          100% { transform: translateY(200vh) scale(0.25); opacity: 0; }
        }

        @keyframes dataStream {
          0% { transform: translateY(-100%) translateX(-50%) rotate(45deg); opacity: 0; }
          50% { opacity: 1; }
          100% { transform: translateY(100%) translateX(50%) rotate(45deg); opacity: 0; }
        }

        @keyframes ripple {
          0% { transform: scale(0.8); opacity: 1; }
          100% { transform: scale(1.2); opacity: 0; }
        }

        .animate-spin-slow {
          animation: spin 3s linear infinite;
        }

        .animate-ripple {
          animation: ripple 2s linear infinite;
        }

        @keyframes computeFlow {
          0% { transform: translateY(-100%); }
          100% { transform: translateY(100%); }
        }

        @keyframes blink {
          0%, 100% { opacity: 0.3; }
          50% { opacity: 1; }
        }

        @keyframes dataTransfer {
          0% { transform: translateX(-100%); opacity: 0; }
          50% { transform: translateX(0); opacity: 1; }
          100% { transform: translateX(100%); opacity: 0; }
        }

        @keyframes progressBar {
          0% { width: 0%; }
          50% { width: 100%; }
          50.1% { width: 0%; }
          100% { width: 0%; }
        }

        @keyframes barHeight {
          0%, 100% { height: 30%; }
          50% { height: 100%; }
        }

        @keyframes slideText {
          0% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }

        @keyframes flowParticle {
          0% { 
            transform: translateY(-100%) scale(1);
            opacity: 0;
          }
          50% { 
            transform: translateY(50%) scale(1.5);
            opacity: 1;
          }
          100% { 
            transform: translateY(200%) scale(1);
            opacity: 0;
          }
        }

        @keyframes lightBeam {
          0% { 
            opacity: 0;
            transform: translateX(-50px) scaleY(0.8);
          }
          50% { 
            opacity: 1;
            transform: translateX(0) scaleY(1);
          }
          100% { 
            opacity: 0;
            transform: translateX(50px) scaleY(0.8);
          }
        }
      `}</style>
    </div>
  );
};

export default NFTMining;
