import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate, library } = useWeb3React();
  const [amount, setAmount] = useState('');
  const [userContribution, setUserContribution] = useState(0);
  const [totalRaised, setTotalRaised] = useState(0);
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  const idoInfo = {
    totalSupply: 100000000,
    privateSale: {
      amount: 10000000,
      bnbTarget: 769.23,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 2
    },
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00'
  };

  // 获取已筹集的BNB数量
  const fetchTotalRaised = async () => {
    if (library) {
      try {
        const balance = await library.getBalance(receivingAddress);
        setTotalRaised(parseFloat(library.utils.fromWei(balance, 'ether')));
      } catch (error) {
        console.error('获取已筹集BNB失败:', error);
      }
    }
  };

  // 定期更新筹集数量
  useEffect(() => {
    fetchTotalRaised();
    const interval = setInterval(fetchTotalRaised, 15000); // 每15秒更新一次
    return () => clearInterval(interval);
  }, [library]);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  // 在交易成功后更新总量
  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.privateSale.minContribution || bnbAmount > idoInfo.privateSale.maxContribution) {
      alert(`请输入${idoInfo.privateSale.minContribution}-${idoInfo.privateSale.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
      setUserContribution(prev => prev + bnbAmount);
      fetchTotalRaised(); // 立即更新总筹集量
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 via-gray-800 to-black">
      {/* 顶部背景装饰 - 降低z-index */}
      <div className="absolute top-0 left-0 w-full h-96 bg-gradient-to-r from-yellow-400/10 via-yellow-500/5 to-yellow-400/10 blur-3xl -z-10"></div>

      <div className="relative container mx-auto px-4 py-16">
        {/* 标题区域 */}
        <div className="text-center mb-16">
          <h1 className="text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-yellow-500 mb-6">
            AIMiner (DPAP) IDO
          </h1>
          <div className="flex items-center justify-center gap-6 text-xl">
            <div className="bg-gray-800/50 backdrop-blur px-6 py-3 rounded-full border border-yellow-400/20">
              <span className="text-yellow-400">总量: </span>
              <span className="text-white font-bold">1亿 DPAP</span>
            </div>
            <div className="bg-gray-800/50 backdrop-blur px-6 py-3 rounded-full border border-yellow-400/20">
              <span className="text-yellow-400 mr-2">⏰</span>
              <span className="text-white">{idoInfo.startTime} - {idoInfo.endTime}</span>
            </div>
          </div>
        </div>

        {/* 主内容区域 */}
        <div className="max-w-4xl mx-auto">
          <div className="bg-gray-800/40 backdrop-blur-xl rounded-3xl border border-gray-700/50 overflow-hidden">
            {/* 我的参与信息 */}
            {active && (
              <div className="bg-gradient-to-r from-yellow-400/20 to-yellow-500/10 p-8 border-b border-gray-700/50">
                <h3 className="text-2xl font-bold text-yellow-400 mb-6">我的参与</h3>
                <div className="grid grid-cols-2 gap-8">
                  <div>
                    <p className="text-gray-400 text-sm mb-2">已参与数量</p>
                    <p className="text-3xl font-bold text-white">{userContribution} <span className="text-yellow-400">BNB</span></p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm mb-2">预计获得</p>
                    <p className="text-3xl font-bold text-white">
                      {(userContribution * idoInfo.privateSale.dpapPerBNB).toLocaleString()} <span className="text-yellow-400">DPAP</span>
                    </p>
                  </div>
                </div>
              </div>
            )}

            <div className="p-8">
              {/* IDO信息网格 */}
              <div className="grid grid-cols-2 gap-6 mb-8">
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">发行总量</p>
                  <p className="text-2xl font-bold text-white">1亿 <span className="text-yellow-400">DPAP</span></p>
                </div>
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">私募总量</p>
                  <p className="text-2xl font-bold text-white">1000万 <span className="text-yellow-400">DPAP</span></p>
                </div>
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">募资目标</p>
                  <p className="text-2xl font-bold text-white">769.23 <span className="text-yellow-400">BNB</span></p>
                </div>
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">兑换比例</p>
                  <p className="text-2xl font-bold text-white">1 BNB = <span className="text-yellow-400">13,000 DPAP</span></p>
                </div>
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">私募价格</p>
                  <p className="text-2xl font-bold text-white">≈ <span className="text-yellow-400">0.05U</span></p>
                </div>
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">参与限额</p>
                  <div>
                    <p className="text-lg font-bold text-white">
                      <span className="text-yellow-400">0.1</span> - <span className="text-yellow-400">2</span> BNB
                    </p>
                    <p className="text-sm text-gray-400 mt-1">1,300 - 26,000 DPAP</p>
                  </div>
                </div>
                <div className="bg-gray-900/50 rounded-2xl p-6">
                  <p className="text-gray-400 text-sm mb-2">募资进度</p>
                  <p className="text-2xl font-bold text-white">{totalRaised.toFixed(2)} <span className="text-yellow-400">BNB</span></p>
                  <div className="mt-2 w-full h-2 bg-gray-700 rounded-full overflow-hidden">
                    <div 
                      className="h-full bg-yellow-400 rounded-full transition-all duration-500"
                      style={{ 
                        width: `${Math.min((totalRaised / idoInfo.privateSale.bnbTarget) * 100, 100)}%` 
                      }}
                    />
                  </div>
                  <p className="text-sm text-gray-400 mt-2">
                    {((totalRaised / idoInfo.privateSale.bnbTarget) * 100).toFixed(2)}% 已完成
                  </p>
                </div>
              </div>

              {/* 参与表单 */}
              {!active ? (
                <button
                  onClick={connectWallet}
                  className="w-full bg-gradient-to-r from-yellow-400 to-yellow-500 text-black font-bold py-4 px-6 rounded-xl hover:from-yellow-500 hover:to-yellow-600 transition-all text-lg"
                >
                  连接钱包参与
                </button>
              ) : (
                <div className="space-y-6">
                  <div className="bg-gray-900/50 rounded-2xl p-6">
                    <label className="block text-gray-400 text-sm mb-3">
                      输入参与数量
                    </label>
                    <div className="relative">
                      <input
                        type="number"
                        min={idoInfo.privateSale.minContribution}
                        max={idoInfo.privateSale.maxContribution}
                        step="0.1"
                        value={amount}
                        onChange={(e) => {
                          const value = parseFloat(e.target.value);
                          if (value > idoInfo.privateSale.maxContribution) {
                            setAmount(idoInfo.privateSale.maxContribution.toString());
                          } else {
                            setAmount(e.target.value);
                          }
                        }}
                        className="w-full bg-gray-800 border-2 border-gray-700 rounded-xl px-4 py-4 text-white text-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all [appearance:textfield] [&::-webkit-outer-spin-button]:appearance-none [&::-webkit-inner-spin-button]:appearance-none"
                        placeholder="0.0"
                      />
                      <div className="absolute right-4 top-1/2 -translate-y-1/2 flex items-center">
                        <span className="text-yellow-400 font-bold text-lg">BNB</span>
                      </div>
                    </div>
                    {amount && (
                      <div className="mt-4 bg-yellow-400/10 rounded-xl p-4">
                        <p className="text-gray-400 text-sm">预计获得</p>
                        <p className="text-xl font-bold text-yellow-400">
                          {(parseFloat(amount || 0) * idoInfo.privateSale.dpapPerBNB).toLocaleString()} DPAP
                        </p>
                      </div>
                    )}
                  </div>

                  <button
                    onClick={handleContribute}
                    className="w-full bg-gradient-to-r from-yellow-400 to-yellow-500 text-black font-bold py-4 px-6 rounded-xl hover:from-yellow-500 hover:to-yellow-600 transition-all text-lg"
                  >
                    确认参与
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IDO;
