import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';
import { ethers } from 'ethers';

// Ëøô‰∫õÂú∞ÂùÄÈúÄË¶ÅÊõøÊç¢‰∏∫ÂÆûÈôÖÈÉ®ÁΩ≤ÁöÑÂêàÁ∫¶Âú∞ÂùÄ
const ZONE_TOKEN_ADDRESS = "YOUR_TOKEN_CONTRACT_ADDRESS";
const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";

// ‰ª£Â∏ÅABI
const ZONE_TOKEN_ABI = [
  "function approve(address spender, uint256 amount) public returns (bool)",
  "function allowance(address owner, address spender) public view returns (uint256)"
];

// Áõ≤ÁõíABI
const MYSTERY_BOX_ABI = [
  "function openBox() public returns (uint8)",
  "function BOX_PRICE() public view returns (uint256)"
];

const NFT_SETTINGS = {
  N: { probability: 0.55, power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { probability: 0.15, power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { probability: 0.05, power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { probability: 0.01, power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MysteryBox = () => {
  const { active, account, activate, library } = useWeb3React();
  const [openingResult, setOpeningResult] = useState(null);
  const [isOpening, setIsOpening] = useState(false);
  const [isApproving, setIsApproving] = useState(false);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const checkAllowance = async () => {
    try {
      const tokenContract = new ethers.Contract(ZONE_TOKEN_ADDRESS, ZONE_TOKEN_ABI, library.getSigner());
      const allowance = await tokenContract.allowance(account, MYSTERY_BOX_ADDRESS);
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const boxPrice = await boxContract.BOX_PRICE();
      return allowance.gte(boxPrice);
    } catch (error) {
      console.error('Error checking allowance:', error);
      return false;
    }
  };

  const approveToken = async () => {
    try {
      setIsApproving(true);
      const tokenContract = new ethers.Contract(ZONE_TOKEN_ADDRESS, ZONE_TOKEN_ABI, library.getSigner());
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const boxPrice = await boxContract.BOX_PRICE();

      // ÊéàÊùÉ‰∏Ä‰∏™ËæÉÂ§ßÁöÑÊï∞È¢ùÔºåÈÅøÂÖçÈ¢ëÁπÅÊéàÊùÉ
      const approveAmount = boxPrice.mul(1000);
      const tx = await tokenContract.approve(MYSTERY_BOX_ADDRESS, approveAmount);
      await tx.wait();

      setIsApproving(false);
      return true;
    } catch (error) {
      console.error('Error approving token:', error);
      setIsApproving(false);
      return false;
    }
  };

  const openBox = async () => {
    if (!active) {
      alert('ËØ∑ÂÖàËøûÊé•Èí±ÂåÖ');
      return;
    }

    try {
      setIsOpening(true);

      // Ê£ÄÊü•ÊéàÊùÉ
      const hasAllowance = await checkAllowance();
      if (!hasAllowance) {
        const approved = await approveToken();
        if (!approved) {
          setIsOpening(false);
          alert('ÊéàÊùÉÂ§±Ë¥•');
          return;
        }
      }

      // Ë∞ÉÁî®ÂêàÁ∫¶ÂºÄÁÆ±
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await boxContract.openBox();
      const receipt = await tx.wait();

      // ËøôÈáåÈúÄË¶Å‰ªéÂêàÁ∫¶‰∫ã‰ª∂‰∏≠Ëé∑ÂèñÂÆûÈôÖÁöÑÂºÄÁÆ±ÁªìÊûú
      // ÁõÆÂâç‰ΩøÁî®Ê®°ÊãüÊï∞ÊçÆ
      const random = Math.random();
      let result = null;

      if (random <= NFT_SETTINGS.SSR.probability) {
        result = { type: 'SSR', ...NFT_SETTINGS.SSR };
      } else if (random <= NFT_SETTINGS.SSR.probability + NFT_SETTINGS.SR.probability) {
        result = { type: 'SR', ...NFT_SETTINGS.SR };
      } else if (random <= NFT_SETTINGS.SSR.probability + NFT_SETTINGS.SR.probability + NFT_SETTINGS.R.probability) {
        result = { type: 'R', ...NFT_SETTINGS.R };
      } else {
        result = { type: 'N', ...NFT_SETTINGS.N };
      }

      setOpeningResult(result);
    } catch (error) {
      console.error('Error opening box:', error);
      alert('ÂºÄÁÆ±Â§±Ë¥•: ' + error.message);
    } finally {
      setIsOpening(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Â∑¶‰æßÂÆùÁÆ±Âå∫Âüü */}
        <div className="bg-gray-800 p-6 rounded-lg flex flex-col items-center justify-center space-y-6">
          <h1 className="text-2xl font-bold text-binance-yellow">Á•ûÁßòÂÆùÁÆ±</h1>
          <div className="w-48 h-48 bg-gray-700 rounded-lg flex items-center justify-center">
            {/* ËøôÈáåÂèØ‰ª•ÊîæÂÆùÁÆ±ÂõæÁâá */}
            <div className="text-6xl">üì¶</div>
          </div>
          {active ? (
            <button
              onClick={openBox}
              disabled={isOpening}
              className={`w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity ${
                isOpening ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {isOpening ? 'ÂºÄÂêØ‰∏≠...' : 'ÂºÄÂêØÂÆùÁÆ± (100 ZONE)'}
            </button>
          ) : (
            <button
              onClick={connectWallet}
              className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
            >
              ËøûÊé•Èí±ÂåÖ
            </button>
          )}
        </div>

        {/* Âè≥‰æßÂºÄÁÆ±ÁªìÊûúÂíåËØ¥Êòé */}
