import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const NFT_SETTINGS = {
  N: { probability: 0.55, power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { probability: 0.15, power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { probability: 0.05, power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { probability: 0.01, power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MysteryBox = () => {
  const { active, account, activate } = useWeb3React();
  const [openingResult, setOpeningResult] = useState(null);
  const [isOpening, setIsOpening] = useState(false);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const openBox = async () => {
    if (!active) {
      alert('请先连接钱包');
      return;
    }

    setIsOpening(true);

    // 模拟开箱过程
    setTimeout(() => {
      const random = Math.random();
      let result = null;

      if (random <= NFT_SETTINGS.SSR.probability) {
        result = { type: 'SSR', ...NFT_SETTINGS.SSR };
      } else if (random <= NFT_SETTINGS.SSR.probability + NFT_SETTINGS.SR.probability) {
        result = { type: 'SR', ...NFT_SETTINGS.SR };
      } else if (random <= NFT_SETTINGS.SSR.probability + NFT_SETTINGS.SR.probability + NFT_SETTINGS.R.probability) {
        result = { type: 'R', ...NFT_SETTINGS.R };
      } else {
        result = { type: 'N', ...NFT_SETTINGS.N };
      }

      setOpeningResult(result);
      setIsOpening(false);
    }, 2000);
  };

  return (
    <div className="max-w-4xl mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* 左侧宝箱区域 */}
        <div className="bg-gray-800 p-6 rounded-lg flex flex-col items-center justify-center space-y-6">
          <h1 className="text-2xl font-bold text-binance-yellow">神秘宝箱</h1>
          <div className="w-48 h-48 bg-gray-700 rounded-lg flex items-center justify-center">
            {/* 这里可以放宝箱图片 */}
            <div className="text-6xl">📦</div>
          </div>
          {active ? (
            <button
              onClick={openBox}
              disabled={isOpening}
              className={`w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity ${
                isOpening ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {isOpening ? '开启中...' : '开启宝箱 (100 ZONE)'}
            </button>
          ) : (
            <button
              onClick={connectWallet}
              className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
            >
              连接钱包
            </button>
          )}
        </div>

        {/* 右侧开箱结果和说明 */}
        <div className="space-y-6">
          {openingResult && (
            <div className="bg-gray-800 p-6 rounded-lg text-center animate-fade-in">
              <h3 className="text-xl font-bold mb-4">开箱结果</h3>
              <p className="text-3xl text-binance-yellow mb-4">
                恭喜获得 {openingResult.type} NFT!
              </p>
              <div className="grid grid-cols-2 gap-4 text-sm">
                <div className="bg-gray-700 p-3 rounded-lg">
                  <p className="text-gray-400">基础算力</p>
                  <p className="text-xl font-bold">{openingResult.power}</p>
                </div>
                <div className="bg-gray-700 p-3 rounded-lg">
                  <p className="text-gray-400">日收益</p>
                  <p className="text-xl font-bold">{openingResult.dailyReward} ZONE</p>
                </div>
                <div className="bg-gray-700 p-3 rounded-lg">
                  <p className="text-gray-400">回本天数</p>
                  <p className="text-xl font-bold">{openingResult.roi}天</p>
                </div>
                <div className="bg-gray-700 p-3 rounded-lg">
                  <p className="text-gray-400">年化收益</p>
                  <p className="text-xl font-bold">{openingResult.yearReturn}%</p>
                </div>
              </div>
            </div>
          )}

          <div className="bg-gray-800 p-6 rounded-lg">
            <p className="font-bold mb-4 text-binance-yellow">NFT稀有度说明</p>
            <div className="space-y-3">
              <div className="flex justify-between items-center p-2 bg-gray-700 rounded-lg">
                <span>SSR - 超级稀有 (1%)</span>
                <span className="text-binance-yellow">算力: 6400</span>
              </div>
              <div className="flex justify-between items-center p-2 bg-gray-700 rounded-lg">
                <span>SR - 稀有 (5%)</span>
                <span className="text-binance-yellow">算力: 1600</span>
              </div>
              <div className="flex justify-between items-center p-2 bg-gray-700 rounded-lg">
                <span>R - 普通稀有 (15%)</span>
                <span className="text-binance-yellow">算力: 400</span>
              </div>
              <div className="flex justify-between items-center p-2 bg-gray-700 rounded-lg">
                <span>N - 普通 (55%)</span>
                <span className="text-binance-yellow">算力: 100</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MysteryBox;
