import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const Navbar = () => {
  const { active, account, activate } = useWeb3React();
  const location = useLocation();

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  return (
    <nav className="bg-binance-dark border-b border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex">
            <div className="flex-shrink-0 flex items-center">
              <span className="text-binance-yellow text-xl font-bold">AIMiner</span>
            </div>
            <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
              <Link
                to="/"
                className={`${
                  location.pathname === '/'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                IDO
              </Link>
              <Link
                to="/mystery-box"
                className={`${
                  location.pathname === '/mystery-box'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                宝箱
              </Link>
              <Link
                to="/mining"
                className={`${
                  location.pathname === '/mining'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                挖矿
              </Link>
              <Link
                to="/market"
                className={`${
                  location.pathname === '/market'
                    ? 'border-binance-yellow text-binance-yellow'
                    : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-gray-100'
                } inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium`}
              >
                市场
              </Link>
            </div>
          </div>
          <div className="flex items-center">
            {!active ? (
              <button
                onClick={connectWallet}
                className="bg-binance-yellow text-binance-dark px-4 py-2 rounded-lg text-sm font-medium hover:opacity-90 transition-opacity"
              >
                连接钱包
              </button>
            ) : (
              <div className="text-gray-300 text-sm">
                {account.slice(0, 6)}...{account.slice(-4)}
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
