import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  const idoInfo = {
    totalSupply: 100000000,
    privateSale: {
      amount: 10000000,
      bnbTarget: 769.23,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 15
    },
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00'
  };

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.privateSale.minContribution || bnbAmount > idoInfo.privateSale.maxContribution) {
      alert(`请输入${idoInfo.privateSale.minContribution}-${idoInfo.privateSale.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-black py-12 px-4">
      <div className="max-w-6xl mx-auto">
        {/* IDO Banner */}
        <div className="text-center mb-12">
          <h1 className="text-5xl font-bold mb-6 bg-gradient-to-r from-yellow-400 via-yellow-300 to-yellow-200 bg-clip-text text-transparent">
            AIMiner (DPAP) IDO
          </h1>
          <div className="inline-flex items-center px-4 py-2 rounded-full bg-gray-800/50 border border-yellow-400/20">
            <span className="text-yellow-400 mr-2">⏰</span>
            <p className="text-gray-300">
              {idoInfo.startTime} - {idoInfo.endTime}
            </p>
          </div>
        </div>

        <div className="grid lg:grid-cols-2 gap-8">
          {/* 左侧：私募详情 */}
          <div className="space-y-8">
            {/* 私募具体信息 */}
            <div className="bg-gray-800/40 backdrop-blur rounded-2xl p-6 border border-gray-700/50">
              <h2 className="text-2xl font-bold mb-6 bg-gradient-to-r from-yellow-400 to-yellow-200 bg-clip-text text-transparent">
                私募详情
              </h2>
              <div className="grid gap-6">
                <div className="bg-gray-800/80 rounded-xl p-5 space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">私募总量</span>
                    <span className="text-white font-medium">1000万 DPAP</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">募资目标</span>
                    <span className="text-white font-medium">769.23 BNB</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">兑换比例</span>
                    <span className="text-white font-medium">1 BNB = 13,000 DPAP</span>
                  </div>
                </div>

                <div className="bg-gray-800/80 rounded-xl p-5 space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">参与额度</span>
                    <span className="text-white font-medium">0.1 - 15 BNB</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">最小购买</span>
                    <span className="text-white font-medium">0.1 BNB = 1,300 DPAP</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">最大购买</span>
                    <span className="text-white font-medium">15 BNB = 195,000 DPAP</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* 右侧：参与表单 */}
          <div className="bg-gray-800/40 backdrop-blur rounded-2xl p-8 border border-gray-700/50 h-fit">
            <h2 className="text-2xl font-bold mb-8 bg-gradient-to-r from-yellow-400 to-yellow-200 bg-clip-text text-transparent">
              参与私募
            </h2>

            {!active ? (
              <button
                onClick={connectWallet}
                className="w-full bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-4 px-6 rounded-xl hover:opacity-90 transition-all shadow-lg shadow-yellow-500/20"
              >
                连接钱包
              </button>
            ) : (
              <div className="space-y-6">
                <div className="bg-gray-800/80 p-4 rounded-xl border border-gray-700">
                  <p className="text-sm text-gray-400 mb-1">当前钱包地址</p>
                  <p className="font-mono text-yellow-400 break-all">{account}</p>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    参与数量 (BNB)
                  </label>
                  <div className="relative">
                    <input
                      type="number"
                      min={idoInfo.privateSale.minContribution}
                      max={idoInfo.privateSale.maxContribution}
                      step="0.1"
                      value={amount}
                      onChange={(e) => setAmount(e.target.value)}
                      className="w-full bg-gray-800/80 border border-gray-700 rounded-xl px-4 py-3 text-white focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
                      placeholder="输入参与数量"
                    />
                    <span className="absolute right-4 top-3 text-gray-400">BNB</span>
                  </div>
                  <div className="mt-3 p-3 bg-yellow-400/10 rounded-lg">
                    <p className="text-sm text-yellow-400">
                      预计获得：{amount ? (amount * idoInfo.privateSale.dpapPerBNB).toLocaleString() : '0'} DPAP
                    </p>
                  </div>
                </div>

                <button
                  onClick={handleContribute}
                  className="w-full bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-4 px-6 rounded-xl hover:opacity-90 transition-all shadow-lg shadow-yellow-500/20"
                >
                  确认参与
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default IDO;
