import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const NFT_TYPES = {
  SSR: { probability: 0.05, powerBoost: 5.0 },
  SR: { probability: 0.15, powerBoost: 3.0 },
  R: { probability: 0.30, powerBoost: 2.0 },
  N: { probability: 0.50, powerBoost: 1.0 }
};

const MINING_POWER_IF_NO_NFT = 100; // 未开出NFT时获得的基础算力

const MysteryBox = () => {
  const { active, account, activate } = useWeb3React();
  const [openingResult, setOpeningResult] = useState(null);
  const [isOpening, setIsOpening] = useState(false);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const openBox = async () => {
    if (!active) {
      alert('请先连接钱包');
      return;
    }

    setIsOpening(true);

    // 模拟开箱过程
    setTimeout(() => {
      const random = Math.random();
      let result = {
        type: null,
        powerBoost: 0,
        gotNFT: false
      };

      // 40%概率开出NFT
      if (random <= 0.4) {
        let nftRandom = Math.random();
        if (nftRandom <= NFT_TYPES.SSR.probability) {
          result = { type: 'SSR', powerBoost: NFT_TYPES.SSR.powerBoost, gotNFT: true };
        } else if (nftRandom <= NFT_TYPES.SSR.probability + NFT_TYPES.SR.probability) {
          result = { type: 'SR', powerBoost: NFT_TYPES.SR.powerBoost, gotNFT: true };
        } else if (nftRandom <= NFT_TYPES.SSR.probability + NFT_TYPES.SR.probability + NFT_TYPES.R.probability) {
          result = { type: 'R', powerBoost: NFT_TYPES.R.powerBoost, gotNFT: true };
        } else {
          result = { type: 'N', powerBoost: NFT_TYPES.N.powerBoost, gotNFT: true };
        }
      } else {
        result = {
          type: null,
          powerBoost: 0,
          gotNFT: false,
          miningPower: MINING_POWER_IF_NO_NFT
        };
      }

      setOpeningResult(result);
      setIsOpening(false);
    }, 2000);
  };

  return (
    <div className="max-w-md mx-auto bg-binance-dark p-6 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-binance-yellow mb-6">神秘宝箱</h1>

      {!active ? (
        <button
          onClick={connectWallet}
          className="w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity"
        >
          连接钱包
        </button>
      ) : (
        <div className="space-y-6">
          <div className="bg-gray-800 p-4 rounded-lg">
            <p className="text-sm text-gray-400">当前钱包地址</p>
            <p className="font-mono">{account}</p>
          </div>

          <div className="text-center">
            <button
              onClick={openBox}
              disabled={isOpening}
              className={`w-full bg-binance-yellow text-binance-dark font-bold py-3 px-4 rounded-lg hover:opacity-90 transition-opacity ${
                isOpening ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {isOpening ? '开启中...' : '开启宝箱'}
            </button>
          </div>

          {openingResult && (
            <div className="bg-gray-800 p-4 rounded-lg text-center">
              <h3 className="text-xl font-bold mb-2">开箱结果</h3>
              {openingResult.gotNFT ? (
                <>
                  <p className="text-2xl text-binance-yellow mb-2">
                    恭喜获得 {openingResult.type} NFT!
                  </p>
                  <p>算力加成: {openingResult.powerBoost}x</p>
                </>
              ) : (
                <>
                  <p className="text-gray-400 mb-2">未开出NFT</p>
                  <p>获得基础算力: {openingResult.miningPower}</p>
                </>
              )}
            </div>
          )}

          <div className="text-sm text-gray-400">
            <p className="font-bold mb-2">NFT稀有度说明:</p>
            <ul className="space-y-1">
              <li>• SSR - 超级稀有 (5%)</li>
              <li>• SR - 稀有 (15%)</li>
              <li>• R - 普通稀有 (30%)</li>
              <li>• N - 普通 (50%)</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default MysteryBox;
