import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  // IDO 信息
  const idoInfo = {
    totalRaise: 1000, // BNB
    raised: 685.5,    // BNB
    price: 0.00001,   // BNB per token
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00',
    minContribution: 0.1,
    maxContribution: 0.5,
  };

  const progress = (idoInfo.raised / idoInfo.totalRaise) * 100;

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.minContribution || bnbAmount > idoInfo.maxContribution) {
      alert(`请输入${idoInfo.minContribution}-${idoInfo.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* IDO Banner */}
      <div className="bg-gradient-to-r from-yellow-600 to-yellow-400 rounded-t-2xl p-8 text-center">
        <h1 className="text-4xl font-bold text-white mb-2">AIMiner IDO私募</h1>
        <p className="text-white/90">参与 AIMiner 生态，把握 AI 挖矿未来</p>
      </div>

      <div className="bg-gray-900 rounded-b-2xl p-8 shadow-2xl">
        {/* IDO Progress */}
        <div className="mb-8">
          <div className="flex justify-between mb-2">
            <span className="text-gray-400">募集进度</span>
            <span className="text-yellow-400 font-medium">{progress.toFixed(2)}%</span>
          </div>
          <div className="w-full bg-gray-800 rounded-full h-4">
            <div 
              className="bg-gradient-to-r from-yellow-600 to-yellow-400 h-4 rounded-full transition-all duration-500"
              style={{ width: `${progress}%` }}
            />
          </div>
          <div className="flex justify-between mt-2 text-sm">
            <span className="text-gray-400">已募集: {idoInfo.raised} BNB</span>
            <span className="text-gray-400">目标: {idoInfo.totalRaise} BNB</span>
          </div>
        </div>

        {/* IDO Info Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div className="bg-gray-800/50 rounded-xl p-6">
            <h3 className="text-lg font-medium text-yellow-400 mb-4">IDO 详情</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-400">代币价格</span>
                <span className="text-white">{idoInfo.price} BNB</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">开始时间</span>
                <span className="text-white">{idoInfo.startTime}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">结束时间</span>
                <span className="text-white">{idoInfo.endTime}</span>
              </div>
            </div>
          </div>

          <div className="bg-gray-800/50 rounded-xl p-6">
            <h3 className="text-lg font-medium text-yellow-400 mb-4">参与规则</h3>
            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-400">最小参与</span>
                <span className="text-white">{idoInfo.minContribution} BNB</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">最大参与</span>
                <span className="text-white">{idoInfo.maxContribution} BNB</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">支付方式</span>
                <span className="text-white">BNB</span>
              </div>
            </div>
          </div>
        </div>

        {/* Contribution Form */}
        <div className="max-w-md mx-auto">
          {!active ? (
            <button
              onClick={connectWallet}
              className="w-full bg-gradient-to-r from-yellow-600 to-yellow-400 text-white font-bold py-4 px-6 rounded-xl hover:opacity-90 transition-all duration-300 transform hover:scale-[1.02]"
            >
              连接钱包
            </button>
          ) : (
            <div className="space-y-6">
              <div className="bg-gray-800/50 p-4 rounded-xl">
                <p className="text-sm text-gray-400 mb-1">当前钱包地址</p>
                <p className="font-mono text-yellow-400">{account}</p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-400 mb-2">
                  参与数量 (BNB)
                </label>
                <div className="relative">
                  <input
                    type="number"
                    min={idoInfo.minContribution}
                    max={idoInfo.maxContribution}
                    step="0.1"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    className="w-full bg-gray-800/50 border border-gray-700 rounded-xl px-4 py-3 text-white focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                    placeholder="输入参与数量"
                  />
                  <span className="absolute right-4 top-3 text-gray-400">BNB</span>
                </div>
              </div>

              <button
                onClick={handleContribute}
                className="w-full bg-gradient-to-r from-yellow-600 to-yellow-400 text-white font-bold py-4 px-6 rounded-xl hover:opacity-90 transition-all duration-300 transform hover:scale-[1.02]"
              >
                确认参与
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default IDO;
