import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';
import { ethers } from 'ethers';

// 这些地址需要替换为实际部署的合约地址
const ZONE_TOKEN_ADDRESS = "YOUR_TOKEN_CONTRACT_ADDRESS";
const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";

// 代币ABI
const ZONE_TOKEN_ABI = [
  "function approve(address spender, uint256 amount) public returns (bool)",
  "function allowance(address owner, address spender) public view returns (uint256)"
];

// 盲盒ABI
const MYSTERY_BOX_ABI = [
  "function openBox() public returns (uint8)",
  "function BOX_PRICE() public view returns (uint256)"
];

const NFT_SETTINGS = {
  N: { probability: 0.55, power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { probability: 0.15, power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { probability: 0.05, power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { probability: 0.01, power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MysteryBox = () => {
  const { active, account, activate, library } = useWeb3React();
  const [openingResult, setOpeningResult] = useState(null);
  const [isOpening, setIsOpening] = useState(false);
  const [isApproving, setIsApproving] = useState(false);
  const [showAnimation, setShowAnimation] = useState(false);

  useEffect(() => {
    if (active && library) {
      // fetchBoxPrice();
    }
  }, [active, library]);

  // const fetchBoxPrice = async () => {
  //   try {
  //     const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
  //     const price = await boxContract.BOX_PRICE();
  //     setBoxPrice(ethers.utils.formatEther(price));
  //   } catch (error) {
  //     console.error('Error fetching box price:', error);
  //   }
  // };

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const checkAllowance = async () => {
    try {
      const tokenContract = new ethers.Contract(ZONE_TOKEN_ADDRESS, ZONE_TOKEN_ABI, library.getSigner());
      const allowance = await tokenContract.allowance(account, MYSTERY_BOX_ADDRESS);
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const boxPrice = await boxContract.BOX_PRICE();
      return allowance.gte(boxPrice);
    } catch (error) {
      console.error('Error checking allowance:', error);
      return false;
    }
  };

  const approveToken = async () => {
    try {
      setIsApproving(true);
      const tokenContract = new ethers.Contract(ZONE_TOKEN_ADDRESS, ZONE_TOKEN_ABI, library.getSigner());
      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const boxPrice = await boxContract.BOX_PRICE();

      const approveAmount = boxPrice.mul(1000);
      const tx = await tokenContract.approve(MYSTERY_BOX_ADDRESS, approveAmount);
      await tx.wait();

      setIsApproving(false);
      return true;
    } catch (error) {
      console.error('Error approving token:', error);
      setIsApproving(false);
      return false;
    }
  };

  const openBox = async () => {
    if (!active) {
      alert('请先连接钱包');
      return;
    }

    try {
      setIsOpening(true);
      setShowAnimation(true);

      const hasAllowance = await checkAllowance();
      if (!hasAllowance) {
        const approved = await approveToken();
        if (!approved) {
          setIsOpening(false);
          setShowAnimation(false);
          alert('授权失败');
          return;
        }
      }

      const boxContract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await boxContract.openBox();
      const receipt = await tx.wait();

      // 模拟开箱动画
      setTimeout(() => {
        setShowAnimation(false);
        setOpeningResult('SSR'); // 这里应该根据实际结果设置
        setIsOpening(false);
      }, 3000);

    } catch (error) {
      console.error('Error opening box:', error);
      setIsOpening(false);
      setShowAnimation(false);
      alert('开箱失败');
    }
  };

  return (
    <div className="min-h-screen bg-[#0f172a] py-16 relative overflow-hidden">
      {/* 背景装饰效果 */}
      <div className="absolute inset-0 overflow-hidden">
        {/* 主渐变背景 */}
        <div className="absolute inset-0 bg-gradient-to-b from-[#0f172a] via-[#1e293b] to-[#0f172a]"></div>

        {/* 光效圆环 */}
        <div className="absolute top-0 left-1/2 -translate-x-1/2 w-[800px] h-[800px] bg-yellow-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-[600px] h-[600px] bg-blue-500/10 rounded-full blur-3xl animate-pulse delay-1000"></div>

        {/* 装饰线条 */}
        <div className="absolute inset-0" style={{
          background: `
            linear-gradient(90deg, transparent 0%, rgba(255,255,255,0.05) 50%, transparent 100%) 0 0 / 200px 1px repeat-y,
            linear-gradient(90deg, transparent 0%, rgba(255,255,255,0.03) 50%, transparent 100%) 100px 0 / 200px 1px repeat-y
          `
        }}></div>

        {/* 星星点缀 */}
        <div className="absolute inset-0" style={{
          background: `radial-gradient(1px 1px at 50% 50%, rgba(255,255,255,0.2) 50%, transparent 100%) 0 0 / 50px 50px repeat`
        }}></div>
      </div>

      {/* 内容区域 */}
      <div className="container mx-auto px-4 relative">
        {/* 头部信息 */}
        <div className="text-center mb-16">
          <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-500 to-yellow-300 mb-4">神秘盲盒</h1>
          <p className="text-xl text-gray-400">开启盲盒，获得稀有 NFT</p>
        </div>

        {/* 主要内容区 */}
        <div className="max-w-3xl mx-auto backdrop-blur-sm bg-white/5 rounded-3xl p-8 shadow-2xl border border-white/10">
          {/* 盲盒图片 */}
          <div className="aspect-square relative mb-12">
            <div 
              className="absolute inset-0 bg-contain bg-center bg-no-repeat"
              style={{ 
                backgroundImage: 'url(/images/mystery-box/box-bg.png)'
              }}
            />
          </div>

          {/* 价格显示 */}
          <div className="text-center mb-8">
            <div className="text-2xl font-bold text-yellow-500">
              100 ZONE
            </div>
          </div>

          {/* 开启按钮 */}
          {active ? (
            <button
              onClick={openBox}
              disabled={isOpening || isApproving}
              className={`w-full max-w-xs mx-auto bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-4 px-8 rounded-full hover:from-yellow-400 hover:to-yellow-300 transition-all flex items-center justify-center ${
                (isOpening || isApproving) ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              {isApproving ? '授权中' : isOpening ? '开启中' : '开启盲盒'}
            </button>
          ) : (
            <button
              onClick={connectWallet}
              className="w-full max-w-xs mx-auto bg-[#1e293b]/50 backdrop-blur-sm text-yellow-500 border-2 border-yellow-500 font-bold py-4 px-8 rounded-full hover:bg-yellow-500 hover:text-black transition-all"
            >
              连接钱包
            </button>
          )}
        </div>

        {/* NFT概率展示 */}
        <div className="max-w-4xl mx-auto">
          <h2 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-yellow-500 to-yellow-300 mb-8 text-center">NFT 概率</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {Object.entries(NFT_SETTINGS).map(([rarity, data]) => (
              <div key={rarity} className="bg-[#1e293b]/50 backdrop-blur-sm rounded-xl p-6 transform hover:scale-105 transition-all">
                <div className={`text-2xl font-bold mb-4 ${
                  rarity === 'SSR' ? 'text-yellow-500' :
                  rarity === 'SR' ? 'text-purple-500' :
                  rarity === 'R' ? 'text-blue-500' :
                  'text-gray-400'
                }`}>
                  {rarity}
                </div>
                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-400">概率</span>
                    <span className="text-white">{data.probability * 100}%</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">算力</span>
                    <span className="text-white">{data.power}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">日收益</span>
                    <span className="text-yellow-500">{data.dailyReward} ZERO</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* 开箱结果弹窗 */}
        {openingResult && (
          <div className="fixed inset-0 bg-black/80 flex items-center justify-center z-50">
            <div className="bg-[#1e293b]/50 backdrop-blur-sm rounded-2xl p-8 max-w-md w-full mx-4 relative border border-white/10">
              <button 
                onClick={() => setOpeningResult(null)}
                className="absolute top-4 right-4 text-gray-400 hover:text-white"
              >
                ✕
              </button>
              <div className="text-center">
                <div className="text-2xl font-bold text-white mb-4">恭喜获得</div>
                <div className={`text-4xl font-bold mb-6 ${
                  openingResult === 'SSR' ? 'text-yellow-500' :
                  openingResult === 'SR' ? 'text-purple-500' :
                  openingResult === 'R' ? 'text-blue-500' :
                  'text-gray-400'
                }`}>
                  {openingResult} NFT
                </div>
                <div className="w-48 h-48 bg-gray-800 rounded-xl mx-auto mb-6"></div>
                <button
                  onClick={() => setOpeningResult(null)}
                  className="bg-yellow-500 text-black font-bold py-3 px-8 rounded-full hover:bg-yellow-400 transition-colors"
                >
                  确定
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MysteryBox;
