import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  // IDO 信息
  const idoInfo = {
    totalSupply: 100000000, // 1亿总量
    privateSale: {
      amount: 10000000, // 1000万
      bnbTarget: 769.23,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 15
    },
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00'
  };

  const tokenDistribution = [
    { name: '私募', amount: '1000万', percentage: '10%', color: 'from-yellow-500 to-yellow-400' },
    { name: '社区空投', amount: '100万', percentage: '3%', color: 'from-blue-500 to-blue-400' },
    { name: '活动空投', amount: '100万', percentage: '2%', color: 'from-green-500 to-green-400' },
    { name: 'NFT挖矿', amount: '7000万', percentage: '60%', color: 'from-purple-500 to-purple-400' },
    { name: '联合坐庄', amount: '1000万', percentage: '10%', color: 'from-red-500 to-red-400' },
    { name: 'LP加池', amount: '500万', percentage: '5%', color: 'from-indigo-500 to-indigo-400' },
    { name: '上市预留', amount: '1000万', percentage: '10%', color: 'from-pink-500 to-pink-400' }
  ];

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.privateSale.minContribution || bnbAmount > idoInfo.privateSale.maxContribution) {
      alert(`请输入${idoInfo.privateSale.minContribution}-${idoInfo.privateSale.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4">
      {/* IDO Banner */}
      <div className="text-center py-12">
        <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-yellow-400 to-yellow-200 bg-clip-text text-transparent">
          AIMiner (DPAP) IDO
        </h1>
        <p className="text-xl text-gray-300 mb-2">总发行量：1亿 DPAP</p>
        <p className="text-gray-400">开始时间：{idoInfo.startTime} | 结束时间：{idoInfo.endTime}</p>
      </div>

      {/* 代币分配信息 */}
      <div className="mb-12">
        <h2 className="text-2xl font-bold mb-6 text-yellow-400">代币分配</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {tokenDistribution.map((item, index) => (
            <div key={index} className="bg-gray-800 rounded-lg p-6 border border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-medium text-white">{item.name}</h3>
                <span className="text-yellow-400 font-bold">{item.percentage}</span>
              </div>
              <div className="w-full bg-gray-700 rounded-full h-2 mb-4">
                <div
                  className={`h-2 rounded-full bg-gradient-to-r ${item.color}`}
                  style={{ width: item.percentage }}
                />
              </div>
              <p className="text-gray-400">总量：{item.amount} DPAP</p>
            </div>
          ))}
        </div>
      </div>

      {/* 私募详情 */}
      <div className="bg-gray-800 rounded-lg p-8 mb-12">
        <h2 className="text-2xl font-bold mb-6 text-yellow-400">私募详情</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          <div className="space-y-4">
            <div className="flex justify-between">
              <span className="text-gray-400">私募总量</span>
              <span className="text-white font-medium">1000万 DPAP</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">募资目标</span>
              <span className="text-white font-medium">769.23 BNB</span>
            </div>
          </div>
          <div className="space-y-4">
            <div className="flex justify-between">
              <span className="text-gray-400">兑换比例</span>
              <span className="text-white font-medium">1 BNB = 13,000 DPAP</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">参与额度</span>
              <span className="text-white font-medium">0.1 - 15 BNB</span>
            </div>
          </div>
          <div className="space-y-4">
            <div className="flex justify-between">
              <span className="text-gray-400">最小购买</span>
              <span className="text-white font-medium">0.1 BNB = 1,300 DPAP</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">最大购买</span>
              <span className="text-white font-medium">15 BNB = 195,000 DPAP</span>
            </div>
          </div>
        </div>
      </div>

      {/* 参与表单 */}
      <div className="max-w-xl mx-auto bg-gray-800 rounded-lg p-8">
        <h2 className="text-2xl font-bold mb-6 text-yellow-400">参与私募</h2>
        {!active ? (
          <button
            onClick={connectWallet}
            className="w-full bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-4 px-6 rounded-lg hover:opacity-90 transition-all"
          >
            连接钱包
          </button>
        ) : (
          <div className="space-y-6">
            <div className="bg-gray-700 p-4 rounded-lg">
              <p className="text-sm text-gray-400 mb-1">当前钱包地址</p>
              <p className="font-mono text-yellow-400">{account}</p>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-400 mb-2">
                参与数量 (BNB)
              </label>
              <div className="relative">
                <input
                  type="number"
                  min={idoInfo.privateSale.minContribution}
                  max={idoInfo.privateSale.maxContribution}
                  step="0.1"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  className="w-full bg-gray-700 border border-gray-600 rounded-lg px-4 py-3 text-white focus:ring-2 focus:ring-yellow-400 focus:border-transparent"
                  placeholder="输入参与数量"
                />
                <span className="absolute right-4 top-3 text-gray-400">BNB</span>
              </div>
              <p className="mt-2 text-sm text-gray-400">
                预计获得：{amount ? (amount * idoInfo.privateSale.dpapPerBNB).toLocaleString() : '0'} DPAP
              </p>
            </div>

            <button
              onClick={handleContribute}
              className="w-full bg-gradient-to-r from-yellow-500 to-yellow-400 text-black font-bold py-4 px-6 rounded-lg hover:opacity-90 transition-all"
            >
              确认参与
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default IDO;
