import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const [userContribution, setUserContribution] = useState(0);
  const [totalRaised, setTotalRaised] = useState(0);
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  const idoInfo = {
    totalSupply: 100000000,
    privateSale: {
      amount: 10000000,
      bnbTarget: 769.23,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 2
    },
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00'
  };

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.privateSale.minContribution || bnbAmount > idoInfo.privateSale.maxContribution) {
      alert(`请输入${idoInfo.privateSale.minContribution}-${idoInfo.privateSale.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
      setUserContribution(prev => prev + bnbAmount);
      setTotalRaised(prev => prev + bnbAmount);
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 背景效果 */}
      <div className="absolute inset-0">
        {/* 网格线 */}
        <div className="absolute inset-0 grid grid-cols-12 grid-rows-8">
          {[...Array(96)].map((_, i) => (
            <div key={i} className="border-[0.5px] border-green-500/5"></div>
          ))}
        </div>
        {/* 顶部光晕 */}
        <div className="absolute top-0 left-0 right-0 h-64 bg-gradient-to-b from-green-500/10 to-transparent"></div>
      </div>

      <div className="relative container mx-auto px-4 py-16">
        {/* 标题区域 */}
        <div className="text-center mb-16 relative">
          {/* 装饰性光环 */}
          <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-96 h-96">
            <div className="absolute inset-0 border-2 border-green-500/20 rounded-full animate-spin-slow"></div>
            <div className="absolute inset-4 border-2 border-green-400/15 rounded-full animate-spin-slow-reverse"></div>
            <div className="absolute inset-8 border-2 border-green-300/10 rounded-full animate-spin-slow"></div>
          </div>

          <h1 className="text-6xl font-bold relative inline-block mb-6">
            <span className="relative z-10 text-transparent bg-clip-text bg-gradient-to-r from-green-400 via-green-500 to-green-600">
              Zero (ZONE) 私募
            </span>
            <div className="absolute -inset-4 bg-green-500/10 blur-2xl rounded-full -z-10"></div>
          </h1>

          <div className="flex items-center justify-center gap-6 text-xl">
            <div className="bg-gray-800/50 backdrop-blur-xl px-8 py-4 rounded-2xl border border-green-500/20 shadow-lg shadow-green-500/10">
              <span className="text-green-400">总量: </span>
              <span className="text-white font-bold">1亿 ZONE</span>
            </div>
            <div className="relative group">
              <div className="absolute -inset-1 bg-gradient-to-r from-green-500 to-green-600 rounded-2xl blur opacity-20 group-hover:opacity-30 transition duration-1000"></div>
              <div className="bg-gray-800/50 backdrop-blur-xl px-8 py-4 rounded-2xl border border-green-500/20 relative">
                <span className="text-green-400 mr-2">⏰</span>
                <span className="text-white">{idoInfo.startTime} - {idoInfo.endTime}</span>
              </div>
            </div>
          </div>
        </div>

        {/* 主内容区域 */}
        <div className="max-w-4xl mx-auto">
          <div className="relative group">
            {/* 发光边框效果 */}
            <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-600 rounded-3xl blur opacity-20 group-hover:opacity-30 transition duration-1000"></div>

            <div className="relative bg-gray-800/40 backdrop-blur-xl rounded-3xl border border-green-500/20 overflow-hidden">
              {/* 我的参与信息 */}
              {active && (
                <div className="bg-gradient-to-r from-green-500/20 via-green-600/20 to-green-500/10 p-8 border-b border-gray-700/50">
                  <h3 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600 mb-6">我的参与</h3>
                  <div className="grid grid-cols-2 gap-8">
                    <div className="relative group">
                      <div className="absolute -inset-1 bg-gradient-to-r from-green-500/20 to-green-600/20 rounded-xl blur-sm opacity-0 group-hover:opacity-100 transition duration-500"></div>
                      <div className="relative bg-gray-900/50 rounded-xl p-6">
                        <p className="text-gray-400 text-sm mb-2">已参与数量</p>
                        <p className="text-3xl font-bold text-white">{userContribution} <span className="text-green-400">BNB</span></p>
                      </div>
                    </div>
                    <div className="relative group">
                      <div className="absolute -inset-1 bg-gradient-to-r from-green-500/20 to-green-600/20 rounded-xl blur-sm opacity-0 group-hover:opacity-100 transition duration-500"></div>
                      <div className="relative bg-gray-900/50 rounded-xl p-6">
                        <p className="text-gray-400 text-sm mb-2">预计获得</p>
                        <p className="text-3xl font-bold text-white">
                          {(userContribution * idoInfo.privateSale.dpapPerBNB).toLocaleString()} <span className="text-green-400">ZONE</span>
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              <div className="p-8">
                {/* IDO信息网格 */}
                <div className="grid grid-cols-2 gap-6 mb-8">
                  {[
                    { label: "发行总量", value: "1亿", unit: "ZONE" },
                    { label: "私募总量", value: "1000万", unit: "ZONE" },
                    { label: "已筹集", value: totalRaised.toFixed(2), unit: "BNB" },
                    { label: "兑换比例", value: "1 BNB = 13,000", unit: "ZONE" },
                    { label: "私募价格", value: "≈ 0.05", unit: "U" },
                    { 
                      label: "参与限额", 
                      value: "0.1 - 2", 
                      unit: "BNB",
                      subtext: "1,300 - 26,000 ZONE" 
                    }
                  ].map((item, index) => (
                    <div key={index} className="relative group">
                      <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500/20 to-green-600/20 rounded-xl blur opacity-0 group-hover:opacity-100 transition duration-500"></div>
                      <div className="relative bg-gray-900/50 rounded-xl p-6 hover:bg-gray-900/60 transition duration-300">
                        <p className="text-gray-400 text-sm mb-2">{item.label}</p>
                        <div>
                          <p className="text-2xl font-bold text-white">
                            <span className="text-green-400">{item.value}</span> {item.unit}
                          </p>
                          {item.subtext && (
                            <p className="text-sm text-gray-400 mt-1">{item.subtext}</p>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* 参与表单 */}
                {!active ? (
                  <button
                    onClick={connectWallet}
                    className="w-full relative group overflow-hidden bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-4 px-6 rounded-xl hover:from-green-600 hover:to-green-700 transition-all text-lg"
                  >
                    <span className="relative z-10">连接钱包参与</span>
                    <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/10 to-transparent transition-transform duration-1000"></div>
                  </button>
                ) : (
                  <div className="space-y-6">
                    <div className="relative group">
                      <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500/20 to-green-600/20 rounded-xl blur opacity-0 group-hover:opacity-100 transition duration-500"></div>
                      <div className="relative bg-gray-900/50 rounded-xl p-6">
                        <label className="block text-gray-400 text-sm mb-3">
                          输入参与数量
                        </label>
                        <div className="relative">
                          <input
                            type="number"
                            min={idoInfo.privateSale.minContribution}
                            max={idoInfo.privateSale.maxContribution}
                            step="0.1"
                            value={amount}
                            onChange={(e) => {
                              const value = parseFloat(e.target.value);
                              if (value > idoInfo.privateSale.maxContribution) {
                                setAmount(idoInfo.privateSale.maxContribution.toString());
                              } else {
                                setAmount(e.target.value);
                              }
                            }}
                            className="w-full bg-gray-800/80 border-2 border-green-500/20 rounded-xl px-4 py-4 text-white text-xl focus:ring-2 focus:ring-green-400 focus:border-transparent transition-all [appearance:textfield] [&::-webkit-outer-spin-button]:appearance-none [&::-webkit-inner-spin-button]:appearance-none"
                            placeholder="0.0"
                          />
                          <div className="absolute right-4 top-1/2 -translate-y-1/2 flex items-center">
                            <span className="text-green-400 font-bold text-lg">BNB</span>
                          </div>
                        </div>
                        {amount && (
                          <div className="mt-4 bg-gradient-to-r from-green-500/10 via-green-600/10 to-green-500/10 rounded-xl p-4">
                            <p className="text-gray-400 text-sm">预计获得</p>
                            <p className="text-xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600">
                              {(parseFloat(amount || 0) * idoInfo.privateSale.dpapPerBNB).toLocaleString()} ZONE
                            </p>
                          </div>
                        )}
                      </div>
                    </div>

                    <button
                      onClick={handleContribute}
                      className="w-full relative group overflow-hidden bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-4 px-6 rounded-xl hover:from-green-600 hover:to-green-700 transition-all text-lg"
                    >
                      <span className="relative z-10">确认参与</span>
                      <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/10 to-transparent transition-transform duration-1000"></div>
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IDO;
