import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Home = () => {
  const navigate = useNavigate();

  const fadeInUp = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  const staggerContainer = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0f172a] to-[#1e293b] text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Hero Section with Animated Background */}
        <motion.div 
          initial="hidden"
          animate="visible"
          variants={staggerContainer}
          className="relative text-center mb-24 overflow-hidden"
        >
          <div className="absolute inset-0 bg-[url('/images/grid.svg')] opacity-10"></div>
          <motion.h1 
            variants={fadeInUp}
            className="text-6xl md:text-7xl font-bold mb-6 bg-clip-text text-transparent bg-gradient-to-r from-yellow-400 to-yellow-600"
          >
            ZERO
          </motion.h1>
          <motion.p 
            variants={fadeInUp}
            className="text-xl md:text-2xl text-gray-300 mb-12 max-w-3xl mx-auto"
          >
            革新性的 AI NFT 挖矿平台，结合 AI 与区块链技术，开启数字资产新时代
          </motion.p>
          <motion.div 
            variants={fadeInUp}
            className="flex flex-col sm:flex-row justify-center gap-6"
          >
            <button 
              onClick={() => navigate('/nft-mining')} 
              className="px-8 py-4 bg-gradient-to-r from-yellow-400 to-yellow-600 rounded-xl text-black font-bold text-lg hover:from-yellow-500 hover:to-yellow-700 transform hover:scale-105 transition-all shadow-lg"
            >
              开始挖矿
            </button>
            <button 
              onClick={() => navigate('/ido')} 
              className="px-8 py-4 bg-gradient-to-r from-gray-800 to-gray-900 border-2 border-yellow-500 rounded-xl text-yellow-500 font-bold text-lg hover:from-yellow-500 hover:to-yellow-600 hover:text-black transform hover:scale-105 transition-all"
            >
              参与 IDO
            </button>
          </motion.div>
        </motion.div>

        {/* 核心特点 - 带动画的卡片 */}
        <motion.div 
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          variants={staggerContainer}
          className="mb-24"
        >
          <motion.h2 
            variants={fadeInUp}
            className="text-4xl font-bold mb-12 text-center"
          >
            核心特点
          </motion.h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                icon: "🎯",
                title: "AI 智能合约",
                features: ["智能合约自动执行", "交易安全透明", "收益实时结算"]
              },
              {
                icon: "🎁",
                title: "神秘盲盒系统",
                features: ["四种稀有度 NFT", "独特外观设计", "特殊属性加成"]
              },
              {
                icon: "💎",
                title: "NFT 交易市场",
                features: ["自由交易 NFT", "实时市场行情", "安全交易保障"]
              }
            ].map((feature, index) => (
              <motion.div
                key={index}
                variants={fadeInUp}
