import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const [userContribution, setUserContribution] = useState(0);
  const [totalRaised, setTotalRaised] = useState(0);
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  const idoInfo = {
    totalSupply: 100000000,
    privateSale: {
      amount: 10000000,
      bnbTarget: 769.23,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 2
    },
    startTime: '2025-02-10 20:00:00',
    endTime: '2025-02-15 20:00:00'
  };

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const handleContribute = async () => {
    if (!active || !account) {
      alert('请先连接钱包');
      return;
    }

    const bnbAmount = parseFloat(amount);
    if (isNaN(bnbAmount) || bnbAmount < idoInfo.privateSale.minContribution || bnbAmount > idoInfo.privateSale.maxContribution) {
      alert(`请输入${idoInfo.privateSale.minContribution}-${idoInfo.privateSale.maxContribution} BNB之间的数量`);
      return;
    }

    try {
      const web3 = window.ethereum;
      await web3.request({
        method: 'eth_sendTransaction',
        params: [{
          from: account,
          to: receivingAddress,
          value: '0x' + (bnbAmount * 1e18).toString(16)
        }]
      });
      setUserContribution(prev => prev + bnbAmount);
      setTotalRaised(prev => prev + bnbAmount);
    } catch (error) {
      console.error('转账失败:', error);
      alert('转账失败，请重试');
    }
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 背景动画效果 */}
      <div className="absolute inset-0">
        {/* 电路背景 */}
        <div className="absolute inset-0">
          {/* 网格线 */}
          <div className="absolute inset-0 grid grid-cols-12 grid-rows-8">
            {[...Array(96)].map((_, i) => (
              <div key={i} className="border-[0.5px] border-green-500/5"></div>
            ))}
          </div>
          {/* 渐变背景 */}
          <div className="absolute inset-0 bg-gradient-to-b from-green-500/5 via-transparent to-green-500/5"></div>
        </div>
      </div>

      {/* 主要内容 */}
      <div className="relative container mx-auto px-4 py-8">
        <div className="text-center mb-12">
          <div className="relative inline-block">
            <div className="absolute -inset-2 bg-green-500/20 rounded-lg blur-lg"></div>
            <h1 className="relative text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600">
              Zero (ZONE) IDO
            </h1>
          </div>
          <p className="text-green-400/80 mt-4">参与 ZERO 代币的初始代币发行</p>
        </div>

        {/* IDO 卡片 */}
        <div className="max-w-3xl mx-auto">
          <div className="bg-[#1e2839] rounded-xl p-6 border border-green-500/20">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* 左侧信息 */}
              <div className="space-y-6">
                <div>
                  <h3 className="text-green-400 font-medium mb-2">代币信息</h3>
                  <div className="space-y-2 text-gray-300">
                    <p>代币名称：ZERO</p>
                    <p>代币符号：ZONE</p>
                    <p>代币总量：1亿 ZONE</p>
                    <p>IDO 价格：0.001 BNB</p>
                  </div>
                </div>
                <div>
                  <h3 className="text-green-400 font-medium mb-2">时间信息</h3>
                  <div className="space-y-2 text-gray-300">
                    <p>开始时间：{idoInfo.startTime}</p>
                    <p>结束时间：{idoInfo.endTime}</p>
                  </div>
                </div>
              </div>

              {/* 右侧购买 */}
              <div className="bg-[#151a24] p-6 rounded-lg border border-green-500/10">
                <h3 className="text-green-400 font-medium mb-4">我的参与</h3>
                {active && (
                  <div className="space-y-4">
                    <div>
                      <p className="text-gray-400 text-sm mb-2">已参与数量</p>
                      <p className="text-3xl font-bold text-white">{userContribution} <span className="text-green-400">BNB</span></p>
                    </div>
                    <div>
                      <p className="text-gray-400 text-sm mb-2">预计获得</p>
                      <p className="text-3xl font-bold text-white">
                        {(userContribution * idoInfo.privateSale.dpapPerBNB).toLocaleString()} <span className="text-green-400">ZONE</span>
                      </p>
                    </div>
                  </div>
                )}
                <h3 className="text-green-400 font-medium mb-4">参与购买</h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm text-green-400/80 mb-2">购买数量 (BNB)</label>
                    <input
                      type="number"
                      value={amount}
                      onChange={(e) => setAmount(e.target.value)}
                      className="w-full bg-[#0B1120] border border-green-500/20 rounded-lg px-4 py-2 text-white focus:outline-none focus:border-green-500 transition-colors"
                      placeholder="输入 BNB 数量"
                    />
                    <p className="text-sm text-green-400/60 mt-1">
                      预计获得：{(amount * idoInfo.privateSale.dpapPerBNB).toFixed(2)} ZONE
                    </p>
                  </div>

                  <button
                    onClick={handleContribute}
                    disabled={!active}
                    className={`w-full bg-gradient-to-r from-green-500 to-green-600 text-white font-medium py-3 px-4 rounded-lg hover:from-green-600 hover:to-green-700 transition-all ${
                      !active ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    {!active ? '请先连接钱包' : '确认参与'}
                  </button>
                </div>

                {/* 进度条 */}
                <div className="mt-6">
                  <div className="flex justify-between text-sm text-green-400/80 mb-2">
                    <span>已筹集</span>
                    <span>{totalRaised.toFixed(2)} BNB</span>
                  </div>
                  <div className="h-2 bg-[#0B1120] rounded-full overflow-hidden">
                    <div
                      className="h-full bg-gradient-to-r from-green-500 to-green-600 transition-all duration-500"
                      style={{ width: `${(totalRaised / idoInfo.privateSale.bnbTarget) * 100}%` }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IDO;
