import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend);

const IDO = () => {
  const { active, account, activate } = useWeb3React();
  const [amount, setAmount] = useState('');
  const receivingAddress = '0x43846d8C3E4308Ac1AF9Bc5AE8453Ed73ede7034';

  // 代币分配数据
  const tokenAllocation = {
    labels: ['私募', '社区空投', '活动空投', 'NFT挖矿', '联合坐庄', 'LP加池', '上市预留'],
    datasets: [{
      data: [10, 3, 2, 60, 10, 5, 10],
      backgroundColor: [
        '#FF6384',
        '#36A2EB',
        '#FFCE56',
        '#4BC0C0',
        '#9966FF',
        '#FF9F40',
        '#7CBA3B'
      ]
    }]
  };

  // IDO 信息
  const idoInfo = {
    totalSupply: 100000000,
    privateSale: {
      amount: 10000000,
      priceInBNB: 0.1,
      dpapPerBNB: 13000,
      minContribution: 0.1,
      maxContribution: 15,
      totalBNBTarget: 769.23,
      totalUSDTarget: 500000,
      pricePerToken: 0.05
    }
  };

  const handleContribute = async () => {
    if (!active) {
      alert('请先连接钱包');
      return;
    }

    if (!amount || amount < idoInfo.privateSale.minContribution || amount > idoInfo.privateSale.maxContribution) {
      alert('请输入有效的金额');
      return;
    }

    // TODO: 实现实际的合约调用逻辑
  };

  return (
    <div className="max-w-6xl mx-auto px-4">
      {/* IDO 标题部分 */}
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-yellow-400 to-yellow-200 bg-clip-text text-transparent">
          AIMiner (DPAP) IDO
        </h1>
        <p className="text-xl text-gray-300">
          总发行量：1亿 DPAP
        </p>
      </div>

      <div className="grid md:grid-cols-2 gap-8 mb-12">
        {/* 代币分配图表 */}
        <div className="bg-gray-800 rounded-lg p-6">
          <h2 className="text-xl font-bold mb-4 text-yellow-400">代币分配</h2>
          <div className="aspect-square">
            <Pie data={tokenAllocation} options={{
              plugins: {
                legend: {
                  position: 'right',
                  labels: {
                    color: '#fff'
                  }
                }
              }
            }} />
          </div>
        </div>

        {/* 私募详情 */}
        <div className="bg-gray-800 rounded-lg p-6">
          <h2 className="text-xl font-bold mb-4 text-yellow-400">私募详情</h2>
          <div className="space-y-4">
            <div className="flex justify-between">
              <span className="text-gray-300">私募总量</span>
              <span className="text-white font-medium">1000万 DPAP (10%)</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-300">募资目标</span>
              <span className="text-white font-medium">769.23 BNB (≈$500,000)</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-300">代币价格</span>
              <span className="text-white font-medium">1 DPAP = $0.05</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-300">兑换比例</span>
              <span className="text-white font-medium">1 BNB = 13,000 DPAP</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-300">最小购买</span>
              <span className="text-white font-medium">0.1 BNB (1,300 DPAP)</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-300">最大购买</span>
              <span className="text-white font-medium">15 BNB (195,000 DPAP)</span>
            </div>
          </div>
        </div>
      </div>

      {/* 其他代币分配详情 */}
      <div className="grid md:grid-cols-3 gap-6 mb-12">
        <div className="bg-gray-800 rounded-lg p-6">
          <h3 className="text-lg font-bold mb-3 text-yellow-400">NFT挖矿 (60%)</h3>
          <p className="text-gray-300">总量：7000万 DPAP</p>
        </div>
        <div className="bg-gray-800 rounded-lg p-6">
          <h3 className="text-lg font-bold mb-3 text-yellow-400">联合坐庄 (10%)</h3>
          <p className="text-gray-300">总量：1000万 DPAP</p>
        </div>
        <div className="bg-gray-800 rounded-lg p-6">
          <h3 className="text-lg font-bold mb-3 text-yellow-400">上市预留 (10%)</h3>
          <p className="text-gray-300">总量：1000万 DPAP</p>
        </div>
      </div>

      {/* 参与私募表单 */}
      <div className="bg-gray-800 rounded-lg p-6 max-w-xl mx-auto">
        <h2 className="text-xl font-bold mb-6 text-yellow-400">参与私募</h2>
        <div className="space-y-4">
          <div>
            <label className="block text-gray-300 mb-2">BNB 数量</label>
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              min={idoInfo.privateSale.minContribution}
              max={idoInfo.privateSale.maxContribution}
              step="0.1"
              className="w-full bg-gray-700 border border-gray-600 rounded-md px-4 py-2 text-white focus:outline-none focus:border-yellow-400"
              placeholder="输入 BNB 数量"
            />
            <p className="text-sm text-gray-400 mt-1">
              预计获得：{amount ? (amount * idoInfo.privateSale.dpapPerBNB).toLocaleString() : '0'} DPAP
            </p>
          </div>
          <button
            onClick={handleContribute}
            disabled={!active}
            className="w-full bg-yellow-500 text-black py-3 px-6 rounded-md font-medium hover:bg-yellow-400 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {active ? '参与私募' : '请先连接钱包'}
          </button>
          <p className="text-sm text-gray-400 text-center">
            支付地址：{receivingAddress}
          </p>
        </div>
      </div>
    </div>
  );
};

export default IDO;
