import React, { useState, useEffect } from 'react';
import { useWeb3React } from '@web3-react/core';
import { injected } from '../utils/connectors';
import { ethers } from 'ethers';

const MYSTERY_BOX_ADDRESS = "YOUR_MYSTERY_BOX_CONTRACT_ADDRESS";

const MYSTERY_BOX_ABI = [
  "function getNFTAttributes(uint256 tokenId) external view returns (tuple(uint8 rarity, uint256 power, uint256 dailyReward, uint256 maxReward, uint256 minedAmount, bool isStaked, uint256 stakeTime))",
  "function getStakedNFTs(address user) external view returns (uint256[])",
  "function calculateReward(uint256 tokenId) public view returns (uint256)",
  "function unstakeNFT(uint256 tokenId) external"
];

const NFT_RARITY = ['N', 'R', 'SR', 'SSR'];

const NFT_SETTINGS = {
  N: { power: 100, price: 100, dailyReward: 2.8, maxReward: 400, roi: 35.7, yearReturn: 308 },
  R: { power: 400, price: 100, dailyReward: 10, maxReward: 100, roi: 10, yearReturn: 900 },
  SR: { power: 1600, price: 100, dailyReward: 40, maxReward: 600, roi: 2.5, yearReturn: 2400 },
  SSR: { power: 6400, price: 100, dailyReward: 160, maxReward: 2400, roi: 0.6, yearReturn: 9600 }
};

const MINING_LEVELS = [
  { name: 'LV1', min: 0, max: 10000, bonus: 0.10, requiredPeople: 5, bonusPerPerson: 0.05 },
  { name: 'LV2', min: 10001, max: 30000, bonus: 0.15, requiredPeople: 8, bonusPerPerson: 0.08 },
  { name: 'LV3', min: 30001, max: 100000, bonus: 0.20, requiredPeople: 12, bonusPerPerson: 0.12 },
  { name: 'LV4', min: 100001, max: 500000, bonus: 0.30, requiredPeople: 18, bonusPerPerson: 0.18 },
  { name: 'LV5', min: 500001, max: Infinity, bonus: 0.50, requiredPeople: 25, bonusPerPerson: 0.25 }
];

const NFT_PROBABILITIES = {
  N: 0.55,
  R: 0.15,
  SR: 0.05,
  SSR: 0.01
};

const NFTMining = () => {
  const { active, account, activate, library } = useWeb3React();
  const [miningStats, setMiningStats] = useState({
    totalPower: 0,
    personalPower: 0,
    efficiency: 0,
    currentRewards: 0,
    dailyRewards: 0,
    teamMembers: 0,
    miningLevel: 'LV1'
  });
  const [nfts, setNfts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const connectWallet = async () => {
    try {
      await activate(injected);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const loadMiningData = async () => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());

      // 加载质押的NFT
      const stakedTokenIds = await contract.getStakedNFTs(account);
      const stakedNFTsData = await Promise.all(
        stakedTokenIds.map(async (tokenId) => {
          const attributes = await contract.getNFTAttributes(tokenId);
          const reward = await contract.calculateReward(tokenId);

          return {
            id: tokenId.toString(),
            type: NFT_RARITY[attributes.rarity],
            power: attributes.power.toString(),
            dailyReward: ethers.utils.formatEther(attributes.dailyReward),
            maxReward: ethers.utils.formatEther(attributes.maxReward),
            minedAmount: ethers.utils.formatEther(attributes.minedAmount),
            currentReward: ethers.utils.formatEther(reward)
          };
        })
      );
      setNfts(stakedNFTsData);

      // 计算总算力和每日收益
      const totalPower = stakedNFTsData.reduce((sum, nft) => sum + parseInt(nft.power), 0);
      const dailyRewards = stakedNFTsData.reduce((sum, nft) => sum + parseFloat(nft.dailyReward), 0);

      setMiningStats(prev => ({
        ...prev,
        personalPower: totalPower,
        dailyRewards
      }));

      setIsLoading(false);
    } catch (error) {
      console.error('Error loading mining data:', error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (active) {
      loadMiningData();
    }
  }, [active, account]);

  const handleUnstake = async (nftId) => {
    try {
      const contract = new ethers.Contract(MYSTERY_BOX_ADDRESS, MYSTERY_BOX_ABI, library.getSigner());
      const tx = await contract.unstakeNFT(nftId);
      await tx.wait();

      alert('解除质押成功！');
      loadMiningData();
    } catch (error) {
      console.error('Error unstaking NFT:', error);
      alert('解除质押失败: ' + error.message);
    }
  };

  const calculateMiningLevel = (power) => {
    const level = MINING_LEVELS.find(l => power >= l.min && power <= l.max);
    return level || MINING_LEVELS[0];
  };

  const calculateTeamBonus = (power, members) => {
    const level = calculateMiningLevel(power);
    return Math.min(members, level.requiredPeople) * level.bonusPerPerson;
  };

  return (
    <div className="min-h-screen bg-[#0B1120] relative overflow-hidden">
      {/* 挖矿动画背景 */}
      <div className="absolute inset-0">
        {/* 矿机运行动画 */}
        <div className="absolute inset-0">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute w-1 h-16 bg-green-500/20"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animation: `mine ${Math.random() * 2 + 1}s infinite`,
                animationDelay: `${Math.random() * 2}s`
              }}
            />
          ))}
        </div>

        {/* 电路板背景 */}
        <div 
          className="absolute inset-0 opacity-10"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 50h20l10-10 10 10h20m20 0h20' stroke='%2330FF94' stroke-width='1' fill='none'/%3E%3Cpath d='M50 0v20l10 10-10 10v20m0 20v20' stroke='%2330FF94' stroke-width='1' fill='none'/%3E%3C/svg%3E")`,
            backgroundSize: '100px 100px'
          }}
        />

        {/* 能量波动 */}
        <div className="absolute inset-0">
          <div className="absolute top-0 left-0 w-full h-1/2 bg-gradient-to-b from-green-500/5 to-transparent"></div>
          <div className="absolute bottom-0 left-0 w-full h-1/2 bg-gradient-to-t from-green-500/5 to-transparent"></div>
        </div>
      </div>

      {/* 主要内容 */}
      <div className="relative container mx-auto px-4 py-8">
        {/* 标题区域 */}
        <div className="text-center mb-12">
          <div className="relative inline-block">
            <div className="absolute -inset-2 bg-green-500/20 rounded-lg blur-lg"></div>
            <h1 className="relative text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-400 to-green-600">
              NFT 挖矿
            </h1>
          </div>
          <div className="mt-4 text-green-400/80">实时算力监控 · 智能收益分析</div>
        </div>

        {/* 挖矿状态展示 */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {/* NFT 状态卡片 */}
          <div className="relative group">
            <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
            <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-green-400">NFT 状态</h3>
                <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse"></div>
              </div>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">持有数量</span>
                  <span className="text-green-400 font-bold">{nfts.length}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">总算力</span>
                  <span className="text-green-400 font-bold">{miningStats.personalPower} H/s</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">状态</span>
                  <span className="text-green-400 font-bold">{miningStats.miningLevel}</span>
                </div>
              </div>
            </div>
          </div>

          {/* 收益统计卡片 */}
          <div className="relative group">
            <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
            <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-green-400">收益统计</h3>
                <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse"></div>
              </div>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">当前收益</span>
                  <span className="text-green-400 font-bold">{miningStats.currentRewards} ZONE</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">预计日收益</span>
                  <span className="text-green-400 font-bold">{miningStats.dailyRewards} ZONE</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">累计收益</span>
                  <span className="text-green-400 font-bold">{nfts.reduce((sum, nft) => sum + parseFloat(nft.currentReward), 0)} ZONE</span>
                </div>
              </div>
            </div>
          </div>

          {/* 运行状态卡片 */}
          <div className="relative group">
            <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
            <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-green-400">运行状态</h3>
                <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse"></div>
              </div>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">运行时间</span>
                  <span className="text-green-400 font-bold">24h 30m</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">网络延迟</span>
                  <span className="text-green-400 font-bold">30ms</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-green-300/80">成功率</span>
                  <span className="text-green-400 font-bold">99.9%</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* NFT列表 */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-green-400 mb-4">我的 NFT</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {nfts.map((nft) => (
              <div key={nft.id} className="relative group">
                <div className="absolute -inset-0.5 bg-gradient-to-r from-green-500 to-green-300 rounded-lg opacity-20 group-hover:opacity-30 transition duration-500 blur"></div>
                <div className="relative bg-[#1A2438]/80 backdrop-blur-xl p-6 rounded-lg border border-green-500/20">
                  <div className="aspect-square mb-4 relative overflow-hidden rounded-lg">
                    <div className="absolute inset-0 bg-gradient-to-br from-green-400/20 to-green-600/20"></div>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <span className="text-4xl">🖥️</span>
                    </div>
                  </div>
                  <div className="space-y-2">
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">NFT #{nft.id}</span>
                      <span className="text-green-400 font-bold">{nft.type}</span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-green-300/80">算力</span>
                      <span className="text-green-400 font-bold">{nft.power} H/s</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* 操作按钮 */}
        <div className="flex justify-center gap-4">
          <button className="relative group overflow-hidden bg-gradient-to-r from-green-500 to-green-600 text-white font-bold py-3 px-8 rounded-lg hover:from-green-600 hover:to-green-700 transition-all">
            <span className="relative z-10">开始挖矿</span>
            <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/10 to-transparent transition-transform duration-1000"></div>
          </button>
          <button className="relative group overflow-hidden bg-transparent text-green-500 border-2 border-green-500 font-bold py-3 px-8 rounded-lg hover:bg-green-500 hover:text-white transition-colors">
            <span className="relative z-10">领取收益</span>
            <div className="absolute inset-0 -translate-x-full group-hover:translate-x-0 bg-gradient-to-r from-transparent via-white/10 to-transparent transition-transform duration-1000"></div>
          </button>
        </div>
      </div>

      {/* 添加样式 */}
      <style jsx>{`
        @keyframes mine {
          0% { transform: translateY(0) scale(1); opacity: 0; }
          50% { transform: translateY(100vh) scale(0.5); opacity: 1; }
          100% { transform: translateY(200vh) scale(0.25); opacity: 0; }
        }
      `}</style>
    </div>
  );
};

export default NFTMining;
